                        background-color:#003E5F!important;















                    }































#left_column #posts .notification .avatar:hover {opacity:15 !important;}















.post{opacity:10 !important; border-bottom:0px !important; color:#025D8C}







.post:hover{opacity:12 !important}







.permalink{background-color: #025D8C !important; background-image:none !important}







.active{background-color:#23B0DB!important; color:#23B0DB !important;}







.nav_item_nipple{display:none !important}







.nav_menu{background-color:#23BODB!important; border-bottom:0px !important; color:#23B0DB !important;}























#nav .nav_item.active,







#nav .nav_item .nav_menu,







#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {







background-color:#003E5F!important;







background-image:none !important;







border:0px !important;







color:#23B0DB!important;






}































#nav .nav_item .nav_menu {







border-bottom:0px !important







}































#nav .nav_item .nav_menu a {







color:#ead2c6 !important;







border-top-color:#23B0DB!important;







}















#nav .nav_item .nav_menu a:hover {







color:#107FC9!important;}























.dashboard_switch_blog_menu_header{







background-color:#23B0DB!important;







color:#107FC9 !important;







}















.dashboard_switch_blog_menu_item{







background-color:#1693A5 !important;







color:#1693A5!important;







border:0px !important







}















.dashboard_switch_blog_menu_footer{







background-color:#1693A5 !important;







color:#1693A5!important;







}















.dashboard_switch_blog_menu_wrapper{







background-color:#23B0DB !important;







}































#new_post_notice_container{







color:#23B0DB !important







}































#new_post_notice_container a{







color:#23B0DB !important







}































#dashboard_switch_blog_menu_current,







#dashboard_switch_blog_arrow_and_divider,







#dashboard_switch_blog_arrow_current







{background-image:none !important}























#footer{















color:#FF4848!important;







}











#left_column #posts .notification {







                        background-color:#003E5F!important;







                        color:#003E5F!important;







                        border-bottom-color: #003E5F!important







                    }











