css:
@-moz-document domain("jappy.de"), domain("jappy.at"), domain("jappy.tv") {

body {  
background: #FFF  !important;
}

.site { 
background: #FFF !important;
}

.header li { 
border-top: none !important;
}

.header { 
border: none !important;
background: #FFF !important;
box-shadow:none !important;
}

.header .tabs a {
border:none !important;
}

.stream2 .bubble.yellow .edge { 
background: #FFF;
border:none !important;
}

.pointerLeft { 
background: none !important;
}

.listOfActions .button.blue { 
background: #FFF;
border-bottom: 1px solid #C2C2C2;
border-left: 1px solid #CFCFCF;
border-radius: 3px;
border-right: 1px solid #C2C2C2;
border-top: 1px solid #CFCFCF;
}

#pr .profileIconsFrame { 
background: #FFFFFF;
border: none !important;
}

.contactsOnline { 
border:none !important;
box-shadow:none !important;
}

#pr .welcome .wrapper { 
border:none !important;
}

#pr .pictureRight { 
border: none !important;
box-shadow: none !important;
}

.header .userOptions { 
background: #FFF;
border:none !important;
box-shadow: none !important;
}

.header .userOptions .nickname { 
color: #000 !important;
}

.profileUserStatus .user .nickname { 
background: none !important;
}

.header .userOptions:hover { 
border: none !important;
box-shadow: none !important;
background: #FFF;
}

.header .wrapper { 
text-align: center;
}

.profileUserStatus .navigation a:hover { 
background: #FFF;
border: 1px solid;
color: #000;
}

.profileUserStatus .navigation a { 
background: #FFF;
border: none !important;
}

.profileNavigationList a.active { 
background: #FFF;
color: #000;
}

.profileNavigationList { 
background: #FFFFFF;
border-top: 1px solid #CDCDCD;
}

.profileImageThumb img { 
border-bottom: none !important;;
}

.stream2 .bubble .edge {
border: none !important;
box-shadow: none !important;
}

.header .tabs li.highlight { 
background: #FFF !important;;
}

.siteLeft .headline { 
background: #FFF;
border: none !important;
box-shadow: none !important;
}

.stream2 .addCommentLine { 
background: #FFF;
border: none !important;
}

.attachedPictures.large a.attachedPicture { 
height: AUTO;
width: 200px;
}

.attachedPictures.large .attachedPicture img { 
height: auto;
width: 200px;
}

.tickerReceiverOptions a.public { 
background: #fff !important;
color: #000 !important;
}

.tickerReceiverOptions a.friends { 
background: #fff !important;
color: #000 !important;
}

.tickerReceiverOptions a.theChosenOnes { 
background: #fff !important;
color: #000 !important;
}

.generalRight .options { 
background: #FFF;
border: 1px solid #e1e1e1;
box-shadow: 1px 1px 2px #C6C6C6;
}

.generalRight .options a.button, .generalRight .options input.button { 
border-bottom: 1px solid #e1e1e1;
}

.stream2 .comments .commentEntry { 
border-bottom: none;
}

.stream2 .entry { 
border-bottom: none !important;
}

.stream2 .bubble.yellowThinking:hover .edge { 
background: #FFF;
}

.stream2 .bubble.yellowThinking .edge { 
background: #FFF;
border: #E1E1E1;
}

.stream2 .addCommentLine .buttonSend { 
background: url("http://s1.jappy.tv/i/icon/save.png") 13px 23px no-repeat #b9b9b9;
border: #666;
cursor: none;
content: "";
}

.header .logoFrame {
padding-top: 0px;
height: 55px;
margin-top: -4px;
}

img[alt="Jappy Community"] { 
content:url(http://wiki.jappy.de/images/f/fc/Logo_new.gif);
margin-left: -10px !important;
}

.notifications .wrapper { 
background: #FFF;
}
}
