css:
/*
YeniAntik Ekşi Sözlük Teması
Yazarlar: barisunver, ugr101mmxi
Sürüm: 2.6
Başlık: "yeniantik ekşi sözlük teması"
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eksisozluk.com") {
#a3-toggle{margin-left:5px !important;}
#aside{position:absolute !important;right:0 !important;}
#container,#top-bar{max-width:100% !important;padding:0 20px !important;width:100% !important;}
#content-section{padding-right:350px !important;width:100% !important;}
#content-body {width:100% !important;}
#entry-list article:hover div.feedback,#entry-list article:hover div.info>.options{opacity:1 !important;}
#entry-list footer .favorite-link.favorited {color:green !important;}
#entry-list footer .entry-date{float:right !important;}
#entry-list footer .entry-date:before{content:"(" !important;margin-left:5px;}
#entry-list footer time,#entry-list footer address{font-size:.9em !important;}
#entry-list>li{margin-top:20px !important;}
#entry-list>li article{display:inline-block !important;vertical-align:top !important;width:100% !important;}
#entry-list>li:before{content:none !important;}
#logo{width:140px !important;}
#logo a{background-image:url(http://i.imgur.com/Mn8IgkS.png) !important;}
#main-index-nav {float:none !important;display:inline-block !important;}
#search-textbox{border:1px solid gray !important;border-radius:0 !important;box-shadow:none !important;color:#fff !important;font-size:8pt !important;height:21px !important;margin-top:3px !important;}
#search-textbox:focus{color:#000 !important;}
#site-footer {border-color:#bbb !important;}
#sub-navigation a,#top-bar input[type=submit].primary,#a3-toggle,#top-navigation>ul>li>a,#entry-list footer div.info>.options a{background-color:#566484 !important;background-image:none !important;border:2px outset #a6b4d4 !important;box-shadow:none !important;color:#fff !important;font:8pt/1.7 Arial, sans-serif !important;height:auto !important;padding:0 4px !important;}
#threads .unread{background-color:lightgreen !important;}
#top-navigation{float:left !important;margin-left:-15px !important;}
#top-navigation .new-update{background-color:green !important;}
#topic {margin-right:310px !important;}
#topic h1{margin:10px 0 !important;}
.edittools {border:0 !important;}
.more-data {border:0 !important;background-color:#c6c6c6 !important;}
.more-data:hover {background-color:#c0c0c0 !important;}
.topic-list li:before{float:left !important;content:"\00B7\00A0" !important;;margin-left:-8px !important;}
.topic-list li{border:0 !important;line-height:1.3 !important;margin-left:8px !important;}
.topic-list li a {padding:0 !important;}
.topic-list li a:hover{background-color:silver !important;}
.topic-list li a small{color:#000 !important;font-size:100% !important;position:static !important;}
.topic-list li a small:after,#entry-list footer .entry-date:after{content:")" !important;}
.topic-list li a small:before,#entry-list footer address:before{content:"(" !important;}
a{color:navy !important;}
body,body>header,#sub-navigation{background-color:#ccc !important;font:9pt/1.618 Verdana, sans-serif !important;}
fieldset legend {border-color:#bbb !important;}
h1,h2,h3,h4,h5,h6{font:bold 12pt Verdana, sans-serif !important;}
html.no-touch #index-section {left:10px !important;}
}
