css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(?!docs|mail|groups|plus|maps|play|news|translate|wallet|video|drive).*\\.google\\.(com|uk|de|jp|us|ru|nl|se|be)/(webhp|search)\\?.*\\&?q=.*"){
div#rcnt .col{
width:auto!important;
}

div#hdtbSum, div#hdtbSum *{
line-height: 15px!important;
}

.ab_tnav_wrp, div#hdtbSum, #hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre{
height: 25px!important;
}

div.med:not(#extrares){
min-width:900px!important;
}

div.med:not(#extrares):not(#res) *{
font-size: inherit!important;
margin-top: 20px!important;
}

div.med:not(#extrares):not(#res) a{
background-color: #EEEEEE!important;
}

div.med span.spell_orig{
margin-left: 20px!important;
}

div#rcnt, .sp_cnt, p.ssp {
    margin: 0!important;
}

.sp_cnt, .ssp, #hdtbMenus, #hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre {
    padding-top: 0!important;
    padding-bottom: 0!important;
    margin-top: 0!important;
    height:auto!important;
}

.cpbb, .kpbb, .kprb, .kpgb, .kpgrb, .ksb, .ab_button {
height: auto!important;
padding: 0!important;
}

body.vasq #hdtbMenus.hdtb-td-o {
    top: 26px!important;
}

p.sp_cnt br, div#extrares, div#epbar{
display: none!important;
}

[aria-hidden=false] {visibility: hidden;}
}
