css:
@-moz-document domain("forum-3dcenter.org") {
@media all {
  .tborder {
    border:0 !important;
    border-radius:3px;
  }
  .thead {
    background: linear-gradient(to bottom,  #eda247 0%,#f09a2d 100%) !important; 
    padding:.5em 1em !important;
  }
  .tcat, #collapseobj_friends .thead {
    background: linear-gradient(to bottom,  #ed8523 0%,#ee7500 100%) !important; 
    padding:.5em 1em !important;
  }
  .tcat, .tcat *, .thead {
    color:rgba(0,0,0,0.9) !important;
    text-shadow:1px 1px rgba(192,192,192,0.5);
  }
  .tcat a {
    text-decoration:none !important;
  }
  .tab_header {
    background-position:top left !important;
    padding:.2em !important;
    border-radius:0 3px 0 0;
  }
  .tab_list li {
    border-bottom-left-radius:0;
    border-bottom-right-radius:0;
  }
  .tab_list .thead:hover {
    background:#efcfa7 !important;
    background: linear-gradient(to bottom, #efcfa7 0%,#f09a2d 100%) !important; 
  }
  .content_block {
    padding-top:0 !important;
    border-top-left-radius:0;
    border-top-right-radius:0;
  }
  #collapseobj_infractions table {
    border-spacing:0 !important;
  }
  .alt1, .alt2, .alt1Active {
    padding:.75em 1em !important;
  }
  .alt2[align="right"] {
    background:#E8E8E8 !important;
  }
  .pagenav td {
    padding:.5em !important;
  }
  .pagenav td.thead {
    padding:.5em 1em !important;
  }
  
  div[id^="post_message_"] {
    text-align:justify !important;
    line-height:1.5em !important;
  }
  
  #posts img.user-image, img[src^="customavatars"] {
    max-width:100% !important;
    height:auto !important;
    border-radius:3px;
  }
  .content_block img[src^="customavatars"], .content_block .fixed_width_avatar img {
    max-width:none !important;
    padding:0 !important;
  }
  #posts img.user-image {
    box-shadow:0 0 3px #888;
    background:#eee !important;
  }
  .resized-img-bar {
    display:none !important;
  }
  .fieldset {
    border:1px solid #ccc !important;
    border-radius:3px;
  }
  .QuoteBorder, .spoiler {
    border-radius:3px;
    margin: 5px 0;
  }
  .panel {
    border:0 !important;
  }
  .signature {
    height:120px !important;
    /* overflow-y:auto !important; */
  }
}

}
