css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    domain("gizmodo.com"),
    domain("jalopnik.com"),
    domain("gawker.com"),
    domain("io9.com"),
    domain("lifehacker.com")
{

    /* Top Bar */
    .top-bar{
        position: absolute !important;
    }
    
    /* Main Article */
    .eight.columns{
        width: 900px !important;
    }
    /* Related articles */
    aside{
        display:none;
    }
    /* Images */
    img{margin: 0 auto;} /* center all*/
    img.transform-ku-xlarge{ /* only the big ones */
        width: 100% !important;
    }
    .magnifier.lightBox{display: none;}
    
    /* Footer */
    footer{
        display: none !important;
    }
    
    /* Comments */
    .six.columns.masonry-brick.replybranch.js_replybranch{
        width: 100% !important;
        display: block !important;
        position: static !important;
    }
    /* Comment reload button */
    .js_300-container.reply-conroller{
        display: none !important;
    }
    /* More Comments */
    .six.columns.masonry-brick.js_load-pending{
        width: 100% !important;
    }
    
    /* Sidebar */
    .four.columns.sidebar-module.hide-for-small{
        display: none !important;
    }
}
