css:
@-moz-document domain("pinterest.com") {
html, body{
color: #ddd !important;
background: #111 !important;
}

.headerBackground, .Closeup.canClose {
background-color: rgba(51, 51, 51, 0.9) !important;
}
  
.Button.btn, .Button.close, .Button.moreItems:hover {
background: linear-gradient(#666,#333) !important;
background-color: #444 !important;
text-shadow: none !important;
border: 1px solid #777 !important;
color: #fff !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}
.Button.btn:active, .Button.btn.active, .Button.moreItems:hover:active, .Button.moreItems:hover.active, .Button.btn[disabled], .Button.btn.disabled, .Button.btn.disabled:hover, .Button.btn.disabled:active  {
background: linear-gradient(#333,#666) !important;
border: 1px solid #777 !important;
}

.Header .UserMenu .profileName {
background-color: #444 !important;
color: #fff !important;
}
.UserMenu {
border-color: #777 !important;
}
.ui-TypeaheadField.search .field, .ui-TypeaheadField.fastFollowSearch .field,
.ui-TypeaheadField.search .field:focus, .ui-TypeaheadField.fastFollowSearch .field:focus {
background: #444 !important;
border: 1px solid #777 !important;
color: #fff !important;
}
.Header .ui-TypeaheadField .ui-Typeahead {
background: #444 !important;
color: #fff !important;
}
.Header .ui-TypeaheadField .ui-Typeahead .results .typeaheadName {
color: #eee !important;
}
.Header .ui-TypeaheadField .ui-Typeahead .results .highlighted a {
background: #666 !important;
color: #eee !important;
}

.Pin.summary.summary {
background: #222 !important;
}
.Pin.summary .pinDescription {
color: #bbb !important;
}
.Pin.summary .pinCredits {
border-top: 1px solid #444 !important;
}
.Pin.summary .creditItem:hover {
background: #333 !important;
}
.Pin.summary .creditItem .creditTitle {
color: #aaa !important;
}
.Pin.summary .creditItem .creditName {
color: #ddd !important;
}
.Modal .modalMask,
.Modal .modalContent,
.PinForm.showLink .standardForm>ul {
background: #333 !important;
}
.Modal .modalContent .pinFormHeader {
color: #eee !important;
border-bottom-color: #444 !important;
}
.formFooter, .AboutLinks {
background: #444 !important;
border-top-color: #555 !important;
}
.AboutLinks .skinny {
text-shadow: none !important;
}
.formItemHeading, .standardForm>ul>li>h3, .loginForm>ul>li>h3, .trademarkForm>ul>li>h3 {
color: #eee !important;
}
.formItem, .standardForm>ul>li, .loginForm>ul>li, .trademarkForm>ul>li {
border-top-color: #444 !important;
}
.BoardPicker .boardPickerOuter, .ui-TextField .content, .formTextField, .standardForm input[type="text"], .standardForm input[type="email"], .standardForm input[type="password"], .standardForm input[type="url"], .standardForm textarea, .loginForm input[type="text"], .loginForm input[type="email"], .loginForm input[type="password"], .loginForm input[type="url"], .loginForm textarea, .trademarkForm input[type="text"], .trademarkForm input[type="email"], .trademarkForm input[type="password"], .trademarkForm input[type="url"], .trademarkForm textarea, .UserSelectBase input, .ui-Select {
background: linear-gradient(#555,#444) !important;
background-color: #555 !important;
border-color: #666 !important;
color: #eee !important;
}
.boardPickerInnerWrapper {
background: #666 !important;
border-color: #777 !important;
color: #eee !important;
}
.boardPickerItem {
color: #eee !important;
}
.BoardPicker .boardPickerInnerWrapper .boardPickerItem:hover {
background-color: #888;
}
.Dropdown.userDropdown.compact {
background: #444 !important;
border: 1px solid #777 !important;
border-top-width: 0px !important;
width: 155px !important;
color: #eee !important;
}
.Dropdown li a {
color: #eee !important;
}
.Dropdown li a:hover,
.Button.borderless.addPinDropdown:hover {
background: #666 !important;
}
.positionModuleElement {
background: #444 !important;
}
.positionModuleElement .pinmarkletPromotion {
background: #555 !important;
color: #ddd !important;
}
.positionModuleElement .pinmarkletPromotion p {
color: #ccc !important;
}
.positionModuleElement .pinmarkletPromotion a {
color: #ddd !important;
}
.Button .buttonText, .positionModuleElement .notifSettings {
color: #eee !important;
}
.Button.borderless {
border-bottom-color: #666 !important;
}
.positionModuleElement .notifHeader,
.positionModuleElement .notifFooter {
background: #555 !important;
}
.NotificationItems .notifItem {
background: #444 !important;
border-bottom: 1px solid #666 !important;
color: #ddd !important;
}
.NotificationItems .notifItem.unseen {
background: #555 !important;
}
.NotificationItems .notifItem:hover {
background: #666 !important;
}
.NotificationItems .notifItem .notifMessage>a,
.boardPinsGrid .title {
color: #eee !important;
}
.NotificationItems .notifItem .notifMessage>a:hover {
color: #aaa !important;
}
.PinActionBar {
background: linear-gradient(rgba(81,81,81,0.9),rgba(41,41,41,0.9)) !important;
}
.boardPinsGrid, .Domain,
.Pin.detailed .closeupContentSection {
background: #222 !important;
border-color: #777 !important;
}
.Pin.detailed .sourceFlagWrapper {
background: #444 !important;
border-top-color: #777 !important;
}
.Pin .sourceFlagWrapper a, .Pin .sourceFlagWrapper p, .CloseupBottom .sectionTitle {
color: #ddd !important;
text-shadow: 0 1px #444 !important;
}
.Board.boardCoverImage {
background: #222 !important;
border-color: #777 !important;
}
.Board.boardCoverImage .boardName .title,
.UserBoards .secretBoardWrapper .title {
color: #ddd !important;
}
.UserBoards .secretBoardWrapper, .UserProfileHeader .header, .UserProfileHeader .header .userProfileHeaderName,
.UserProfileHeader .userProfileHeaderRepinsFrom {
background: #444 !important;
color: #eee !important;
text-shadow: none !important;
}
.UserProfileHeader .userProfileHeaderRepinsFrom>h3 {
color: #eee !important;
}
.UserProfileHeader .userProfileHeaderRepinsFrom a,
.UserBoards .secretBoardWrapper a {
color: #bbb !important;
}
.UserProfileHeader .userProfileHeaderRepinsFrom a:hover,
.UserBoards .secretBoardWrapper a:hover {
color: #888 !important;
}
.BoardCreateRep {
color: #bbb !important;
text-shadow: none !important;
}
.InfoBarBase {
background: linear-gradient(#444,#333) !important;
background-color: #444 !important;
border-left: 1px solid rgba(0,0,0,0.2) !important;
color: #eee !important;
}
.InfoBarBase .headerText {
color: #aaa !important;
}
.InfoBarBase .headerText .query {
color: #eee !important;
}
.UserInfoBar .userStats li {
border-right-color: rgba(150,150,150,0.2);
}
.UserInfoBar .followersFollowingLinks li {
border-left-color: rgba(150,150,150,0.2);
}
.InfoBarBase li a, .InfoBarBase li span {
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}
.InfoBarBase li a:hover, .InfoBarBase li a.active, .InfoBarBase li span:hover, .InfoBarBase li span.active {
background: linear-gradient(#333,#444) !important;
background-color: #333 !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}
.InfoBarBase li a, .InfoBarBase li span {
color: #bbb !important;
text-shadow: none !important;
}
.FeedPage .feedName {
color: #eee !important;
text-shadow: none !important;
}
.BoardHeader h1 {
color: #eee !important;
text-shadow: none !important;
}
.BoardHeader .description {
color: #aaa !important;
text-shadow: none !important;
}
.inModal .formTitle, .inModal .standardForm>h1, .inModal .loginForm>h1, .inModal .trademarkForm>h1, .modalStyle .formTitle, .modalStyle .standardForm>h1, .modalStyle .loginForm>h1, .modalStyle .trademarkForm>h1 {
color: #eee !important;
border-bottom: none;
}
.formSection, .standardForm>ul, .loginForm>ul, .trademarkForm>ul {
background-color: #333 !important;
}
.ScrollToTop button {
background: linear-gradient(#444,#333) !important;
background-color: #444 !important;
color: #eee !important;
text-shadow: none !important;
}
.PinBookmarklet .Module, .UserSelectBase .userSelectHeader {
background-color: #333 !important;
}
.UserSelectBase .userSelectBeforeSend .userSelectConnectContent, .UserSelectBase .userSelectBeforeSend .userSelectContent, .ui-ImageUploader, .SearchScopeSelector {
background: #444 !important;
}
.RecentContacts .recentContactsWrapper {
border-bottom-color: #777 !important;
}
.User.userSelect .userName {
color: #eee !important;
}
.UserSelectBase .staticResults li {
border-bottom: 1px solid #777;
}
.Button.userSelectConnect {
background-color: #333 !important;
}
.Button.userSelectConnect:hover {
background-color: #555 !important;
}
.NewPinsIndicator {
background-color: #333 !important;
color: #eee !important;
text-shadow: none !important;
-webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.33),inset 0 0 1px #888;
-moz-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.33),inset 0 0 1px #888;
box-shadow: 0 1px 3px 0 rgba(0,0,0,0.33),inset 0 0 1px #888;
}
.ui-ImageUploader .uploaderProgress {
background-color: #777 !important;
}
.ui-ImageUploader .uploaderProgress span, .fancyToggle .status, .User.gridItem .username, .detailed .pinDescriptionComment .commentDescriptionContent {
color: #eee !important;
}
.AddPinURL .installBookmarklet {
color: #444 !important;
border-bottom-color: #777;
}
.AddPinURL .findPins, .User.gridItem {
background-color: #444 !important;
}
.fancyToggle {
background: linear-gradient(#555,#444) !important;
background-color: #555 !important;
border-color: #666 !important;
}
.fancyToggle .slider {
background: linear-gradient(#555,#444) !important;
background-color: #555 !important;
border-color: #666 !important;
}
input, input[type="password"], input[type="search"], isindex {
  background-color: transparent !important;
}
.PinCommentList.detailed .commentsContainer {
background-color: #222 !important;
}
.PinCommentList.detailed .commentsContainer .PinComment {
border-color: #666 !important;
}
.Pin.detailed .PinCommentList {
border: none !important;
}
.RichPinBase .richPinTitleWrapper.onlyAttribution {
background-color: #555 !important;
border-color: #666 !important;
color: #eee !important;
}
a {
color: #aaa !important;
}
a:hover {
color: #eee !important;
}
.PaddedPin .paddedPinLink {
background-color: #222 !important;
}
.PaddedPin .paddedPinLink:hover {
background-color: #333 !important;
}
}
