css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pcforum.hu") {
h1 { font-size: 200% !important; }
p { max-width: 100% !important; }
body:not(.popup) #pagealign { margin-top: -25px !important; }
body:not(.popup) #page {
min-width: 980px;
max-width: 1600px;
width: 100% !important;
position: relative !important;
}
body:not(.popup) #headerbar .sep:after {
content: '|' !important;
margin: 7px !important;
}
body:not(.popup) #pageloginlink,
body:not(.popup) #pageregisterlink {
height: 26px !important;
line-height: 26px;
}
body:not(.popup) .maincol {
width: 77% !important;
float: right !important;
}
.colset { display: inline-block !important; min-height: 2400px; width: 100%; }
.sidecol {
width: 20% !important;
position: absolute;
top: 0px;
left: 0px;
float: left;
padding-right: 10px !important;
padding-left: 20px !important;
}

.allheader {
width: 74% !important;
float: right !important;
}
.sidelist > ul > li > a:visited {
color: #999999 !important;
}
.pager ul.pages { max-width: 800px !important; }
.inner { background-color: #EEEEEE !important; box-shadow: 1px 1px 3px rgba(0,0,0,0.2) !important; }
.inner:hover { box-shadow: 1px 1px 3px rgba(0,0,0,0.5) !important; }
.forumentrylist div:nth-of-type(2n+0) .inner,
.kbaseentrylist div:nth-of-type(2n+0) .inner {
background-color: #F8F4F4 !important;
}
.forumentrylist div:nth-of-type(2n+1) .inner
.kbaseentrylist div:nth-of-type(2n+1) .inner {
background-color: #EFEAEA !important;
}
.allheader .inner { background-color: #E6E6FF !important; }
.forumentry.solution .inner { background-color: #CCFFCC !important; }
.msg_quote {
background-size: 20px 20px;
min-height: 26px !important;
padding-left: 30px !important;
margin-bottom: 5px !important;
}
.forumlist > ul > li,
.forumlist ul.props > li.entrycount span {
box-shadow: 1px 1px 3px rgba(0,0,0,0.2) !important;
border-radius: 5px !important;
}
.forumlist > ul > li:hover {
box-shadow: 1px 1px 3px rgba(0,0,0,0.5) !important;
}
.sidelist > ul > li {
padding: 4px 3px 4px 12px !important;
margin-bottom: 7px !important;
box-shadow: 1px 1px 3px rgba(0,0,0,0.15) !important;
border-radius: 5px !important;
background-color: #F8F8F8 !important;
}
.actionbutton,
input[type="submit"] {
box-shadow: 1px 1px 3px rgba(0,0,0,0.2) !important;
border-radius: 12px !important;
}
.sidelist > ul > li:hover,
input[type="submit"]:hover,
.actionbutton:hover { box-shadow: 1px 1px 3px rgba(0,0,0,0.5) !important; }
.sidelist > ul li:hover { background-color: #E8E8E8 !important; }
.forumhistory center a { font-size: 180% !important; }
.illu img { max-width: 640px !important; max-height: 360px !important; margin: auto !important; }
.illu { float: none !important; display: block !important; margin: 10px auto !important; width: 640px; }
/* Reklámok eltávolítása */
.adsense, ins, .adsbygoogle, .sharebarcontainer { display: none !important; }
.networkheader { display: none !important; }
}
