css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wassr.jp") {

.Update,
#HeadBox,
.Side-contents,
.Side-hd-title,
.OneTopic,
.Main-contents,
.Main-hd,
#Side,
#Main,
#BodyWrapper,
#Footer,
#PageBody,
#globalnav,
#Header,
#Contents{
background:none !important;
background-image:none !important;
background-color:#fff !important;
padding:0 !important;
margin:0 !important;
float:none !important;
width:auto !Important;
}


#lnav-Public,
h3.ttlLv2,
p.MoreMorePageNav,
/* */
img.inlineicon,
img.viaemoji,
/* */
#My-URL p,
.MySub,
.MyThumb,
/* */
p.UpdateHd,
.SwitchButton,
#globalnav,
.Main-hd,
.PageBody-shd,
#headlink,
#footinfo,
#sideInfo,
#SideBox-Odai,
#SideBox-Invite,
#Side-QR,
.Side-ft,
.Side-hd,
.Main-ft,
.ad_adsense{
display:none !important;
}

p.nm textarea,
#MessageText{
width:97% !important;
height:1.2em !important;
text-align:left;
}

p.submit-btn{
float:left !important;
margin-left:3px !important;
}

h2.ttlLv2{
margin-top:2em !important;
clear:left !important;
}

#mymenu ul {
width:auto !important;
}

.MsgSide{
display:none;
}

p.messagefoot{
display:inline !important;
}

.favorite_list{
display:inline !important;
background:none !important;
border:none !important;
}

ul.UserList li,
ul.UserList,
ul.MessageList li,
ul.MessageList,
.MsgBody,
.OneMsg{
width:auto !important;
float:none !important;
}

.MsgBody a{
text-decoration:none;
}

.MsgBody:hover{
background-color:yellow !important;
}
.MsgBody{
padding:6px 8px !important;
}
.OneMsg {
padding:0 !important;
}

.MsgBody:hover{
background-color:#ffffc0 !important;
}

