css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
.logged_in {
    background: url('http://i.imgur.com/BshXEXZ.gif') bottom left #020100 no-repeat fixed;
  }

.identity.base {
    background: url('http://i.imgur.com/BshXEXZ.gif') bottom left #020100 no-repeat fixed;
  }

.controls_section.user_list li a:before {
  background: none !important;
  border-right: none !important;
}

.controls_section li div.follow {
  color: rgba(255, 255, 255, 0.5);
}

.footer_links a {
  color: rgba(255, 255, 255, 0.5);
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: none;
padding: 20px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}

.plexi.show.color
{
  background: rgba(77, 77, 77, 0.77);
}

#dashboard_controls .dashboard_controls_arrow_first_page
{
  background: url('http://i.imgur.com/wyA9IJ6.png') !important;
  opacity: 1;
}


#dashboard_controls .dashboard_controls_arrow_left
{
  background: url('http://i.imgur.com/a7Sxdna.png') !important;
  opacity: 1;
}

#dashboard_controls .dashboard_controls_arrow_right
{
  background: url('http://i.imgur.com/T57Ro0s.png') !important;
  opacity: 1;
}

#dashboard_controls a
{
  color: #fff;
  text-shadow: #000 1px 1px 1px;
}

#dashboard_controls a:hover
{
  color: #fff;
  opacity: 0.5;
}

#dashboard_controls .control
{
  color: #fff;
  text-shadow: #000 1px 1px 1px;
}

#pagination a
{
  background-color: #000;
  color: #fff;
}

#pagination a:hover
{
  color: #fff;
  opacity: 0.4;
}

.pill.notice
{
  color: #000;
}

#auto_pagination_loader 
{
  background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
  text-shadow: #4E4E4E 2px 2px 3px;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #000;
opacity:0.6;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

.search_form_row
{
  background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
}

.search_query
{
  color: #fff; 
}

#right_column .controls_section
{
  background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
}

#right_column .controls_section .recessed {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-border-bottom-right-radius: 6px;
-webkit-border-bottom-left-radius: 6px;
-moz-border-radius-bottomright: 6px;
-moz-border-radius-bottomleft: 6px;
border-bottom-right-radius: 6px;
border-bottom-left-radius: 6px;
position: relative;

}

#right_column .controls_section .recessed:hover {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;
}

#right_column .controls_section .selected {
background: #31a510;
background: -moz-linear-gradient(top,#31a510 0,#31a510 50%,#31a510 50%,#31a510 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#31a510),color-stop(50%,#31a510),color-stop(50%,#2d8415),color-stop(100%,#2d8415));
-webkit-box-shadow: 0 0 5px #31a510,inset 0 1px 0 rgba(255,255,255,.40);
-moz-box-shadow: 0 0 5px #31a510,inset 0 1px 0 rgba(255,255,255,.40);
box-shadow: 0 0 5px #31a510,inset 0 1px 0 rgba(255,255,255,.40);
-webkit-border-radius: 1px;
-moz-border-radius: 1px;
border-radius: 1px;
border-top: none;
}

#right_column .controls_section .selected:hover {
background: #31a510;
background: -moz-linear-gradient(top,#31a510 0,#31a510 50%,#31a510 50%,#31a510 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#31a510),color-stop(50%,#31a510),color-stop(50%,#2d8415),color-stop(100%,#297813));
-webkit-box-shadow: 0 0 5px #31a510;
-moz-box-shadow: 0 0 5px #31a510;
box-shadow: 0 0 5px #31a510;
border-top: none;
}

#right_column .radar {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
position: relative;
}

#right_column .radar .content.is_media .mask.top {
height: 9px;
background: none;
background-position: 0 0;
top: 0;
}

#right_column .radar .content.is_media .mask.bottom {
height: 19px;
background: none;
background-position: 0 -9px;
bottom: 0;
}

.right_column {
  background: none;
}

img#content_top, img#content_bottom {
display:none;
}
}


@-moz-document url-prefix("http://www.tumblr.com/following"), url-prefix("http://www.tumblr.com/spotlight"), url-prefix("http://www.tumblr.com/lookup") , url-prefix("https://www.tumblr.com/following"), url-prefix("https://www.tumblr.com/spotlight"), url-prefix("https://www.tumblr.com/lookup") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: url('http://assets.tumblr.com/images/content_bg_directory.png?2');
padding: 0px;
padding-bottom: 25px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}
}


@-moz-document url-prefix("http://www.tumblr.com/help") , url-prefix("https://www.tumblr.com/help") {
.help_search {
    border-radius: 12px 12px 12px 12px;
    margin: -20px 0 -20px -20px;
}
}
