css:
/*  Name: Reddit - Dark - sidebar (i.reddit.com) */

/*  A dark css for the iphone version
    of reddit.com */
   
   
/*
  Created by budRich 2012
*/


@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url-prefix("http://www.i.reddit.com/"),
  url-prefix("https://www.i.reddit.com/") {

#topbar, .options_link, .subtoolbar {display: none !important;}

body {
    background: none repeat scroll 0 0 #C5CCD3 !important;
    font-family: Segoe UI,Helvetica,"Helvetica Neue",Arial,sans-serif;
    height: 100%;
    margin: 0;
    width: 100%;
}

.commentcount > .comments {border-image: none !important;}

a {
    color: #EEEEEE !important;
}
a:visited {
    color: #CCCCCC !important;
}

.tagline {
    color: #bbbbbb  !IMPORTANT;
}

.subtoolbar {
    -moz-box-sizing: border-box;
    background: -moz-linear-gradient(center top , #333333, #000000) repeat scroll 0 0 #E5E5E5 !IMPORTANT;
    border-bottom: 1px solid #000000;
    height: 32px;
    overflow: hidden;
    padding: 6px;
    text-overflow: ellipsis;
}
.subtoolbar > ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
}
.subtoolbar > ul > li {
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
}
.subtoolbar > ul > li a {
    color: #4C566C;
    font-size: 12px;
    font-weight: bold;
    line-height: 20px;
    margin: 0;
    overflow: hidden;
    padding: 3px 10px;
    text-decoration: none;
    text-overflow: ellipsis;
}
.subtoolbar > ul > li.selected a {
    background: -moz-linear-gradient(center top , #000000, #000000) repeat scroll 0 0 #C3C3C3  !IMPORTANT;
    border: 1px solid #333333 !IMPORTANT;
    border-radius: 8px 8px 8px 8px;
    box-shadow: none !IMPORTANT;
    padding-bottom: 1px;
    padding-top: 2px;
}

.link {
    -moz-box-sizing: border-box;
    background: none repeat scroll 0 0 #202020  !IMPORTANT;
    border-bottom: 1px solid #202020 !IMPORTANT;
    border-top: 1px solid #202020 !IMPORTANT;
    min-height: 70px;
    overflow: hidden;
    padding: 5px 5px 0;
    position: relative;
}

.link:nth-child(2n+1) {
    background: none repeat scroll 0 0 #282828 !IMPORTANT;
}

.link > .expando {
    background: none repeat scroll 0 0 #333333 !IMPORTANT;
    border: 1px solid #414141 !IMPORTANT;
    border-radius: 8px 8px 8px 8px;
    clear: both;
    font-size: 11px;
    margin: 5px 0 30px;
    padding: 5px;
    color: #DDDDDD  !IMPORTANT;
}

}
