css:
/*
 * copied from http://userstyles.org/styles/64118/ported-wikipedia-beautifier with color removed
 */


@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("wikipedia.org")

{

    #firstHeading {

        font-family: Minion Pro, Palatino, Georgia, "Times New Roman", serif !important;

        width: 900px !important;

        margin-left: auto !important;

        margin-right: auto !important;

        padding-top: 0.25em !important;

        padding-bottom:0.25em !important;

        border: none !important;

        font-weight: bold !important;

    }



    #bodyContent {

        font-family: Minion Pro, Palatino, Georgia, "Times New Roman", serif !important;

        font-size: 1.09em !important;

        line-height: 1.55em !important;
/*
        border: 1px solid #ccc !important;
*/

        width: 900px !important;

        padding: 50px !important;

        margin-left: auto !important;

        margin-right: auto !important;

        margin-top: 1em !important;
/*
        background: #fbfbfb !important;
*/

    }

    

    #mw-articlefeedback {

        font-family: sans-serif !important;

        font-size: 0.994em !important;

    }



    /* Hover to reveal */

    #mw-head, #mw-panel {

        opacity: 0;

        -moz-transition: all 400ms ease-in-out !important;

        -webkit-transition: all 400ms ease-in-out !important;

    }



    #mw-head:hover, #mw-panel:hover {

        opacity: 1;

    }



    .infobox,

    .tright, .tleft, div.tright, div.tleft,

    .taxobox, table.taxobox,

    .wikitable {

        margin-top: 0;

        margin-bottom: 0.5em;

    }



    .ambox, table.ambox {

        margin: 1.5em 10%;

    }



    pre {

        overflow: auto;

    }



}
