css:
@-moz-document
	url-prefix('https://www.tumblr.com/')
{

DIV.l-header-container { background-color: rgba(255, 204, 204, .75); }
DIV.tab_bar .tab.iconic .tab_anchor:hover { opacity: .7}
BUTTON.compose-button {
	background-color: rgb(255, 204, 204);
	border-color: rgb(255, 255, 255);
}
UL.controls_section LI.item:hover:not(.section_header),
UL.controls_section LI.controls_section_item:hover:not(.section_header) {
 background-color: rgb(255, 204, 204);
}
/* blue fix */
/*div.search_form_row {background-color: rgba(255,204,204,0.3)}
div.l-header .selection_nipple {border-bottom-color: rgba(255,204,204,0.3)}*/
#search_query:hover {background: rgba(255,204,204,1)}
#search_query {
  background: linear-gradient(to right, rgba(255,204,204, .8), rgba(255,204,204, 0));
  /*border: thin solid rgb(255, 182, 193) !important;*/
  color: rgb(102, 102, 102);
}
.search_form_field::before {color: rgb(102, 102, 102) !important}
#search_query::-webkit-input-placeholder {
  color: rgb(102, 102, 102);
  opacity: 0.4;
}
#search_query::-moz-placeholder {
  color: rgb(102, 102, 102);
  opacity: 0.4;
}

#left_column/*, #right_column*/ {
	background-color: transparent;
}
#right_column {background-color: rgba(255,204,204,.5)}
.section_header,
.tumblelog_title,
.tumblelog_name,
.radar .radar_tumblelog_label .name,
.radar .radar_tumblelog_label .subheading {color: rgba(102, 102, 102, 1) !important}

/*.follow_list_item_blog:before {
  background-image: none !important;
  border-right: none !important;
  }*/
.radar div.radar_footer .radar_avatar:before {
  background-image: none !important;
  }

.blog.has_follow_button:before,
#right_column:after {display:none !important}

.controls_section {width: auto !important}
.controls_section:hover,
#tumblr_radar:hover {background-color: rgba(255,255,255,.5) !important}

Body {
	color: #565656 !important;
	font-size: 11px !important;
	background-image: none !important;
}

/*--*--*/BODY, DIV, SPAN, P, A {
	font-family: Georgia, Times New Roman, Serif !important;
	font-style: italic !important;
}

a, a:visited {
	color: #666 !important;
}

a:hover {
	color: #333 !important;
}

body:not(.mceContentBody) {
	background: url('https://33.media.tumblr.com/377e31c6641f9edee27a621e90993128/tumblr_ndkq6sA1v21rxghfko2_400.jpg') repeat fixed #513660 top right !important;
}
.l-content {
	background: rgba(0,0,0,0) !important;
}

#dashboard_edit_post {
	background-color: #fff !important;
}

#left_column #posts .post .permalink {
	background-color: #ccc !important;
	border-radius: 0px 11px 0px 0px !important;
}

#left_column #posts .post .post_avatar,

#left_column #posts .post .sub_avatar .sub_avatar_inner {
	background-color: #fbfbfb !important;
	border-bottom: 0px !important;
}

#left_column #posts .post {
	border-bottom: 0px #1f0b1b solid !important;
}

#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
	background-color: #fff !important;
	background-image: none !important;
	border: 0px !important;
	background: rgba(255,255,255,0.4) !important;
	font-family: Georgia !important;
	font-style: italic !important;
}

#nav .nav_item {
	color: #fff !important;
	font-family: Georgia !important;
	font-size: 16px !important;
	font-style: italic !important;
	text-shadow: 1px 1px 2px #444;
	font-weight: bold !important;
}

#nav .nav_item:hover {
	color: #eee !important;
}

#nav .nav_item .nav_menu {
	border-bottom: 0px !important;
}

#nav .nav_item .nav_menu a {
	color: #ccc !important;
	text-shadow: none;
	border-top: 0px !important;
	margin-top: 0px !important;
}

#nav .nav_item .nav_menu a:hover {
	color: #a895b3 !important;
	background: rgba(255,255,255,0.7) !important;
}

#footer {
	color: #666 !important;
	background: rgba(255,255,255,0.8) !important;
	background-image: none !important;
	text-shadow: none !important;
	font-family: Georgia !important;
	font-style: italic !important;
}

#footer a {
	color: #777 !important;
}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, 
h1.dashboard_header span {
	color: #ccc !important;
}

#right_column .dashboard_nav_item {
	border-bottom: 0px solid rgba(0,0,0,0.5) !important;
	background-image: none  !important;
}

#right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item 
ul.dashboard_subpages,

                    #right_column .dashboard_nav_item 
ul.dashboard_subpages li a {
	color: #fff !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
	color: #fff !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title {
	color: #fff !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title a {
	color: #fff !important;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
	background: none !important;
}

#show_radar a, #hide_radar a,
#hide_recommended_blogs a, 
#show_recommended_blogs a {
	color: #fff !important;
	background: none !important;
}

#dashboard_controls_radar_media {
	background: none !important;
}

#dashboard_controls_suggested_blogs {
	background-color: rgba(255,255,255,0.4) !important;
	border: 0px !important;
}

#suggested_blog_avatar_1,#suggested_blog_avatar_2,#suggested_blog_avatar_3 {
	border: 0px !important;
}

.dashboard_controls_suggested_blog {
	border: 0px !important;
}

#left_column #posts .notification {
	background-color: #fbfbfb !important;
	color: #666 !important;
	border-bottom-color: #eee !important;
}

#left_column #posts .notification .block {
	color: #333 !important;
}

#left_column #posts .notification.alt {
	background-color: #f7f7f7 !important;
}

#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
	border-bottom: 0px !important;
}

#left_column #posts .notification a {
	color: #565656 !important;
	border-bottom: 0px !important;
}

#left_column #posts .notification blockquote {
	border-left-color: #eee !important;
}

#left_column #posts .notification .avatar {
	border-color: transparent !important;
	background-color: #fbfbfb !important;
	opacity: 0.5 !important;
}

#left_column #posts .notification .avatar:hover {
	opacity: 1 !important;
	border-bottom: 0px !important;
}

#dashboard_switch_blog_menu {
	background-color: rgba(255,255,255,0.8) !important;
	text-shadow: none !important;
	border: 0px !important;
	font-family: Georgia !important;
	font-style: italic !important;
}

#dashboard_switch_blog_menu_header, 
#dashboard_switch_blog_arrow_and_divider,
.dashboard_switch_blog_menu_item,
#dashboard_switch_blog_menu_footer {
	text-shadow: none !important;
	border: 0px !important;
	color: #555 !important;
	font-family: Georgia !important;
	font-style: italic !important;
}

.dashboard_switch_blog_menu_item a {
	border: 0px !important;
	color: #ccc !important;
}

.dashboard_switch_blog_menu_item:hover {
	background-color: rgba(255,255,255,0.7) !important;
	color: #555 !important;
}

#dashboard_switch_blog_menu_footer {
	background-color: rgba(255,255,255,0.6) !important;
}

#new_post_notice_container {
	color: #fff !important;
	background-image: none !important;
}

#new_post_notice_container a {
	color: #fff !important;
}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current {
	background-image: none !important;
}

.notification_type_icon {
	display: none !important;
}

h1,h2,h3,h4,h5,h6 {
	color: #eee !important;
}

#right_column:after {display:none}

.new_post_label {
  padding: 71px 1px 8px 0 !important;
    /* move the button text vertically (keep top+bottom = 79px) */
}

.new_post_label > I::before {content: none}
A.new_post_label > I {
  height: 90px;
  transition: background-position .08s linear;
  background: url('https://38.media.tumblr.com/3c80f779df0623c1129ca16e6a3fc627/tumblr_ndkq6sA1v21rxghfko1_1280.jpg') no-repeat;
  background-size: 525px auto;
    /* Tumblr's icons are 75x95px each in a 525x95h image, which is
       cropped to 513x95h. These icons are ca. 73x95h in a 513x95h
       image; we'll stretch them so they fit the button faces. */
  z-index: -1;
    /* sink the button faces below the button text */
}

.new_post_label > SPAN {
	text-transform: lowercase;
	font-family: Georgia;
	font-size: 11px;
	font-style: italic;
	/*--margin-top: 8px !important;--*/
	letter-spacing: 4px;
	word-spacing: -10px;
}

#new_post_label_text > I {background-position: 2px -5px !important}
#new_post_label_photo > I {background-position: -73px -5px !important}
#new_post_label_quote > I {background-position: -148px -5px !important}
#new_post_label_link > I {background-position: -223px -5px  !important}
#new_post_label_chat > I {background-position: -298px -5px !important}
#new_post_label_audio > I {background-position: -373px -5px !important}
#new_post_label_video > I {background-position: -448px -5px  !important}

#new_post_label_text:hover > I {background-position: 2px -9px !important}
#new_post_label_photo:hover > I {background-position: -73px -9px !important}
#new_post_label_quote:hover > I {background-position: -148px -9px !important}
#new_post_label_link:hover > I {background-position: -223px -9px  !important}
#new_post_label_chat:hover > I {background-position: -298px -9px !important}
#new_post_label_audio:hover > I {background-position: -373px -9px  !important}
#new_post_label_video:hover > I {background-position: -448px -9px  !important}

#new_post_label_text:active > I {background-position: 2px -5px !important}
#new_post_label_photo:active > I {background-position: -73px -5px !important}
#new_post_label_quote:active > I {background-position: -148px -5px !important}
#new_post_label_link:active > I {background-position: -223px -5px !important}
#new_post_label_chat:active > I {background-position: -298px -5px !important}
#new_post_label_audio:active > I {background-position: -373px -5px !important}
#new_post_label_video:active > I {background-position: -448px -5px !important}


.sub_avatar {
	width: 32px !important;
	height: 32px !important;
	background-color: transparent !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	color: #fff !important;
	text-shadow: none !important;
	margin: 0px !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
	color: #cdcdcd !important;
	text-shadow: none !important;
}

#left_column #posts .post .post_avatar {
	border-radius: 38px !important;
/*--	-webkit-border-radius: 60px !important;--*/
	width: 64px !important;
	height: 64px !important;
	margin: 0px !important;
}

#left_column #posts .post .sub_avatar_inner {
	border-radius: 38px !important;
/*--	-webkit-border-radius: 60px !important;--*/
	width: 32px !important;
	height: 32px !important;
	margin: 0px !important;
}

#dashboard_switch_blog_menu_current {
	color: #fff !important;
	font-family: Georgia !important;
	text-shadow: 1px 1px 2px #444 !important;
	font-style: italic !important;
	text-shadow: none !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item 
ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	color: #fff !important;
	font-family: Georgia !important;
	font-style: italic !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages 
li {
	margin-bottom: 10px !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	background-color: rgba(255,255,255,0.7) !important;
	color: #555 !important;
	border-radius: 10px !important;
	margin: 0px !important;
	padding: 3px  !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
	background-color: rgba(255,255,255,0.8) !important;
	color: #222 !important;
	border-radius: 10px !important;
	padding: 4px  !important;
}

#logo {
	height: 49px;
	width: 210px;
	}
#logo > .logo_anchor {
	background: url('https://31.media.tumblr.com/a730c61eac71c3fda44452f7a14f7778/tumblr_ndkq6sA1v21rxghfko3_250.png') no-repeat;
	}

.selection_nipple {
	display: none !important;
}

.tab a {
	color: #fff !important;
}

.items li a {
	color: #444 !important;
}

.last_item a {
	color: #444 !important;
}

.selected a {
	text-shadow: none !important;
	color: #fff !important;
}

.recessed a {
/*--	text-shadow-color: none !important;--*/
	color: #fefefe !important;
}

.right_column_section li {
	background: rgba(255,255,255,0.4) !important;
	border: 0px !important;
}

.right_column_section li a {
	color: #555 !important;
}

.right_column_section li a:hover {
	background: rgba(255,255,255,0.6) !important;
}

.notification, .avatar_frame, .nipple {
	border: 0px !important;
}

.permalink {
	background-image: none !important;
	height: 15px !important;
	width: 15px !important;
	margin-top: 4px !important;
	margin-right: 5px !important;
	border-radius: 5px !important;
}

/*#left_column, #right_column {background-color: transparent !important;}*/

div.l-content.l-content.l-content {
	background-color: rgba(255,255,255,0) !important;
}

.dashboard_options_form, .dashboard_options_form_disclaimer {
	border: 0px !important;
	color: #fff !important;
	font-weight: bold !important;
	text-shadow: 1px 1px 1px #444 !important;
}

}
