css:
@-moz-document domain("www.chatwork.com") {
@font-face {
  font-family: 'Textar';
  font-style: normal;
  font-weight: normal;
  src: local('MS PGothic'),
       local('Textar'),
       local('IPAMonaPGothic'),
       local('Mona'),
       url('https://dl.dropbox.com/u/520126/u/textar-min.woff') format('woff'),
       url('https://dl.dropbox.com/u/520126/u/textar-min.ttf') format('truetype'),
       url('https://dl.dropbox.com/u/520126/u/textar-min.svg#Textar') format('svg');
}
#_timeLine .chatTimeLineMessageArea pre,
#cw_main .cw_message pre,
#cw_main .cw_message pre > div,
#_chatText, 
#cw_chattext,
.subContentSection pre,
.taskSendTextarea textarea {
  font-family: 'Textar', 'MS PGothic', 'IPAMonaPGothic', 'Mona', sans-serif;
  font-size: 11.5px;
  line-height: 1.125;
  color: #282828;
}
#cw_main .cw_message .ui_sp_favicon {
  -webkit-background-size: 435px 60px;
  -moz-background-size: 435px 60px;
  -ms-background-size: 435px 60px;
  -o-background-size: 435px 60px;
  background-size: 435px 60px;
  background-position: -45px -45px;
  width: 10px;
  height: 10px;
}
#cw_main .cw_message .ui_sp_favicon_parent {
  padding-left: 10px;
}
#cw_main .cw_message .ui_sp_favicon_twitter_com {
  background-position: -390px 0px;
}
#cw_main .cw_message .ui_emoticon {
  width: 10px;
  height: 10px;
}

.avatar,
.avatarMedium,
.avatarSmall,
.taskMetaArea img {
  width: 18px;
  height: 18px;
}
.dateContent {
  left: 1%;
}
.chatTimeLineMessageArea {
  padding: 0 45px 0 26px;
}
.chatTimeLineMessage .chatTimeLineMessageInner {
  padding: 2px;
}
.actionArea {
  bottom: -6px;
}
.actionArea .actionNav {
  font-size: 10px;
  height: 15px;
  line-height: 13px;
}
.actionBarSpacer {
  height: 6px;
}
.promotionArea,
.chatTimeLineItemHeader {
  display: none;
}
.actionArea .actionNav li span:first-child {
  font-size: 10px;
}
.chatListTitleArea {
  line-height: 12px;
  font-size: 10px;
  font-weight: normal;
}
.incomplete {
  line-height: 12px;
}
.incomplete li {
  font-weight: normal;
  font-size: 10px;
}
.chatListMeta {
  top: 2px;
}
.incomplete li span {
  font-size: 12px;
}
.chatListTitleNoLabel {
  height: 24px;
}
.chatListPin {
  top: 5px;
}
.chatListMeta {
  left: 35px;
}
.sideContentMenu {
  height: 100%;
}
.chatFilterMenu {
  height: 28px;
}
.btnHome, .btnCategory,
.chatFilterMenu .btnNavList {
  top: 3px;
}
.chatRoomListArea {
  height: -webkit-calc(100% - 62px) !important;
  height: -moz-calc(100% - 62px) !important;
  height: calc(100% - 62px) !important;
}
.chatroomTitle .chatroomTitleMeta {
  height: 18px;
  line-height: 18px;
  margin-left: 24px;
}
.subContentLabel {
  font-size: 10px;
  font-weight: normal;
}
.subContentLabel p {
  padding: 0 5px;
}
.subContentAreaInner {
  padding: 5px;
}
.button {
  height: 18px;
  min-height: 18px;
  min-width: 18px;
  line-height: 18px;
}
.subContentAreaInner > .button {
  top: 5px;
  right: 5px;
}
.completeCheckboxArea .button,
.chatSendToolbar .button {
  height: 14px;
  min-height: 14px;
  line-height: 14px;
  font-size: 10px;
  padding: 0 5px;
}
.taskAddBtnArea .button {
  font-size: 10px;
  margin: 5px 5px 0;
  padding: 0 5px;
}
.subContentTask > li .btnGroup {
  bottom: 5px;
}
.btnGroup .button + .button {
  margin-left: 5px;
}
.icoSizeLarge {
  font-size: 10px;
}
.showMoreMember {
  font-size: 10px;
  vertical-align: baseline;
}
.timeStamp {
  top: 3px;
  font-size: 10px;
}
.chatSendToolbar {
  height: 20px;
  line-height: 20px;
}
.chatSendTool .icoSizeLarge {
  font-size: 11px;
}
.subContentTask .chatQuote .title {
  margin-left: -20px;
  padding-left: 20px;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}
.taskDateLimit {
  font-size: 10px;
}
.myListShow label {
  font-size: 10px;
}
.subRoomTaskArea .subContentLabel {
  margin-bottom: 5px;
}
.subContentSection {
  margin: 5px;
}
.subContentArea .taskAddArea .subContentAreaInner {
  padding: 5px 10px;
}
.controlGroup {
  min-height: 25px;
}
.controlGroup .controlLabel,
.controlGroup .emptyContent {
  font-size: 10px;
}
.controlGroup .controlLabel {
  width: 20%;
}
.controlGroup .controlContent {
  margin-left: 20%;
}
.subContentArea .taskAddArea .controlGroup .controlContent .taskAssignBox,
.subContentArea .taskAddArea .controlGroup .controlContent .limitBox {
  margin-bottom: 0px;
  padding-bottom: 5px;
}
.assignContent {
  font-size: 10px;
  font-weight: normal;
  height: 14px;
  line-height: 14px;
  vertical-align: top;
}
.contentHl {
  height: 30px;
  line-height: 30px;
}
h1, h2 {
  font-size: 12px;
}
}
