/*--
This style contains non-loading images - http://img576.imageshack.us/img576/8731/69585390.png (TIMEOUT2)
--*/
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

/* blue fix */
div.search_form_row {background-color: rgba(0,0,0,.1)}
div.l-header .selection_nipple {border-bottom-color: rgba(0,0,0,.1)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.blog.has_follow_button:before,
#right_column:after {display:none!important}

body:not(.mceContentBody) {
background: url('http://29.media.tumblr.com/tumblr_lmj7h0fgqf1qapzdwo1_500.gif')
  top center #000 repeat fixed !important;
font-size:11px;}

li.post,
div.l-content.l-content.l-content.l-content {
background: transparent !important;
border:0px!important;
-webkit-box-shadow:none!important;
box-shadow:none!important;}

*{text-shadow:none !important; color:#000000 !important;}

a, a:visited{color:#000000!important;text-decoration:none!important}

a:hover{color:#00c3fe!important}

#nav .nav_item.active,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent !important;
border:0px!important;}

#nav .nav_item .nav_menu {
background: transparent !important;
border-bottom:0px!important;
border-top:0px!important;}

#nav .nav_item .nav_menu a {
color:#000000 !important;
border-bottom:0px!important;
border-top:0px!important;}

#footer, #footer a {
color:#000000 !important;
background-image:none !important;}

#right_column .dashboard_nav_item #hide_radar {
background-image: none !important;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo,
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset,
#right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: none!important;}

#right_column .dashboard_nav_item {
border-bottom:0px solid #000000 !important;
background-image:none!important}

#edit_post_controls .advanced_option_divider{
height:0px!important;
background:#d8d6d6!important;
border-bottom:0px!important}

.no_posts_found, h1.dashboard_header {
color: #000000!important;}

img#content_top, img#content_bottom {
display: none!important;}

#left_column #posts .post .permalink {
display:none !important}

#posts .post .reply_pane .nipple,
.notification_type_icon{
opacity:0 !important;}

#posts .post .post_avatar {
border-bottom: 0px!important;
-webkit-box-shadow:none!important;
box-shadow:none!important;
background-color: transparent!important;}

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:transparent !important;}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none !important;}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}

#posts .post .footer_links.with_tags .source_url{
background-color: transparent!important;}

#category_list {
background: transparent!important;
border: 0px!important;}

#left_column #posts .notification a {
color:#000000 !important;
border-bottom:0px !important;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 0px!important;
border-bottom: 0px!important;}

.follower {
background: rgba(249,252,254,0.1) !important;
border-bottom: 0px!important;
border-top: 0px!important;}

.follower.alt {
background: rgba(249,252,254,0.3) !important;}

.follower img.avatar {
background: transparent!important;
border:0px!important;}

.dashboard_options_form{
background: transparent!important;
border-bottom: 0px!important;
border-top: 0px!important;}

form.dashboard_options_form .dashboard_options_form_divider {
background-color:transparent!important;
border-bottom:0px!important;}

ol#posts li.notification.first_notification {
background: rgba(249,252,254,0.2) !important;}

ol#posts li.notification.alt {
background: rgba(249,252,254,0.1) !important;}

ol#posts li.notification {
background: rgba(249,252,254,0.2) !important;
border-bottom:0px !important;}

#new_post_notice_container{
color:#ffffff !important}

#new_post_notice_container a{
color:#ffffff !important}

.as_links a{
background-color:transparent!important}

#edit_post_controls .advanced_option_divider{
height:0px!important;
background:transparent!important;
border-bottom:0px!important}

ol#posts li.notification.first_notification:hover,
posts li.notification.alt:hover,
ol#posts li.notification:hover {
background: rgba(249,252,254,0.4) !important;
border-bottom:0px !important;}

ol#posts li.notification.last_notification {
border-bottom-width:0px!important;}

#invite_someone{
background: rgba(249,252,254,0.1) !important;
border-bottom:transparent!important}

.post_question_nipple, .arrow{
background:transparent!important;}

.post_question{
background: rgba(249,252,254,0.1) !important;}

#posts ol.notes li {background:transparent!important;}

#posts ol.notes li.answer {
background:transparent!important;}

.answer_container {
background: transparent !important;
border:0px!important;}

#pagination a{
background: rgba(249,252,254,0.1) !important;
border-bottom:0px!important;}

.blue_note{
background:transparent!important;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs{
background: rgba(249,252,254,0.2) !important;
border:0px!important;}

.right_column_promo{
background:transparent!important;}

.right_column_promo_name a{
color:#000!important}

.tag_page_header_subpage.active .tag_page_header_nipple_wrapper{
display:none!important}

/*--#crushes--*/ a .count{
/*--background:url('http://img576.imageshack.us/img576/8731/69585390.png')!important--*/}

#crush_1,#crush_2,#crush_3,#crush_4,#crush_5,#crush_6,#crush_7,#crush_8,#crush_9{
border:0px!important;}

.sub_avatar{
width:32px !important;
height:32px !important;
background-color:transparent !important}

.dashboard_nav_item_shadow{
display:none !important}

#content_top, #content_bottom{display:none!important;}

#dashboard_switch_blog_menu{
border:0px!important;
background:rgba(249,252,254,0.7)!important;}

.dashboard_switch_blog_menu_item{
border:0px!important;
background:rgba(249,252,254,0.7)!important;}

.user_menu .user_menu_list a .user_menu_list_item,
#dashboard_switch_blog_menu_footer{
background:rgba(249,252,254,0.7)!important;
border-bottom: 0px!important;
border-top: 0px!important;}

.user_menu .user_menu_list a .user_menu_list_item,
#dashboard_switch_blog_menu_footer:hover{
background:rgba(249,252,254,0.7)!important;
border-bottom: 0px!important;
border-top: 0px!important;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {
border-top:0px!important;
border-top-width:0px!important;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header {
border-bottom:0px!important;
border-top-width:0px!important;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header #dashboard_switch_blog_arrow_and_divider {
border-top:0px!important;}

#posts .notification.single_notification,
#posts .notification.last_notification {
border-bottom:0px!important;}

#posts .notification .reply_pane, #posts .post .reply_pane,
#posts ol.notes li.reblog,
#posts ol.notes li.like {
background: transparent !important;
border-bottom:0px;}

#nav .nav_item .nav_menu a,
#search_box #search_controls a {
border-bottom: 0px solid #DEE8F1!important;
border-top: 0px solid #FCFDFE!important;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog .dashboard_controls_suggested_blog_title img {
border: 0px!important;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {
border-top:0px!important;
border-top-width:0px!important;}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none !important;}

#inner_footer a:hover{color:#000;}

#inner_footer {background: rgba(0,0,0,0) !important;}

#user_tools .tab.iconic > A {
  background-image: url('data:image/svg+xml,<?xml version="1.0" encoding="UTF-8" standalone="no"?><svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="175" height="36" id="navbar_sprite" version="1.1"><title>Tumblr tab icons</title><style>%23layer1{fill:rgb(239,79,82)}%23layer2{display:none}.icon{fill-opacity:0}</style><defs fill="%23fff"><mask id="m_all"><g id="m_home"><path d="m 8,28 0,-12 9.5,-7.5 9.5,7.5 0,12 -6,0 0,-7 -7,0 0,7 z" /></g><g id="m_inbox"><rect x="41.5" y="10" width="22" height="17" /><g style="stroke:%23000;stroke-width:1.5;stroke-linecap:square"><path d="m 41.5,11.5 11,10 11,-10" /><path d="m 41.5,25.5 7.25,-7.25" /><path d="m 63.5,25.5 -7.25,-7.25" /></g></g><g id="m_help"><circle cx="87.5" cy="18.5" r="11" /><path style="stroke:%23000;stroke-width:2.5" d="m 84.5,16 a 3,2.6875 0 1 1 4.9,2 L 88.75,18.5 a 3,2.6875 0 0 0 -1.25,2.25 l 0,0.75" /><rect style="fill:%23000" x="86.25" y="22.75" width="2.5" height="2.5" /></g><g id="m_settings"><g transform="translate(122.5,18.5)"><circle r="11" /><circle fill="%23000" r="5" /><g id="land4"><g id="land2"><circle id="land" r="2.5" transform="rotate(22.5) translate(11,0)" fill="%23000" /><use xlink:href="%23land" transform="rotate(45)" /></g><use xlink:href="%23land2" transform="rotate(90)" /></g><use xlink:href="%23land4" transform="rotate(180)" /></g></g><g id="m_logout" style="fill:none;stroke:%23fff;stroke-width:4;stroke-linecap:round"><path d="m 151,12 a 9,9 0 1 0 13,0" /><path d="m 157.5,9 0,9" /></g></mask><linearGradient id="myGradient"><stop offset="0%" stop-color="%23F60" /><stop offset="100%" stop-color="%23FF6" /></linearGradient></defs><g id="layer0" mask="url(\'%23m_all\')"><rect id="layer1" width="100%" height="100%" /><image id="layer2" width="100%" height="100%" preserveAspectRatio="xMinYMin slice" xlink:href="http://www.topdesignmag.com/wp-content/uploads/2010/12/137.jpg" /><rect class="icon" id="home" width="20%" height="100%" x="0" /><rect class="icon" id="inbox" width="20%" height="100%" x="20%" /><rect class="icon" id="help" width="20%" height="100%" x="40%" /><rect class="icon" id="settings" width="20%" height="100%" x="60%" /><rect class="icon" id="logout" width="20%" height="100%" x="80%" /></g></svg>') !important;
  }
/*--some earlier incarnation of the tab icons?
#right_column .dashboard_nav_item.active .icon.heart{
background: url('http://img28.imageshack.us/img28/7055/xzz.png') no-repeat transparent!important}

#right_column .dashboard_nav_item.active .icon.gear{
background: url('http://img24.imageshack.us/img24/5340/gearh.png') no-repeat transparent!important}

#right_column .dashboard_nav_item .icon.dude{
background: url('http://img853.imageshack.us/img853/6262/dashboardmasterspriteco.png') no-repeat transparent!important}
--*/

#return_to_top .return_to_top_icon{
background: url('http://26.media.tumblr.com/tumblr_llarhwuQK01qfsn5ro1_100.gif') no-repeat transparent!important}

#posts .post .post_controls .like_button{
background: url('http://img20.imageshack.us/img20/9341/sasdc.png') no-repeat transparent!important}

#posts .post .post_controls .like_button:hover{
background: url('http://img20.imageshack.us/img20/9341/sasdc.png') no-repeat transparent!important}

#posts .post .post_controls .like_button.already_like{
background: url('http://img854.imageshack.us/img854/6679/37970526.png') no-repeat transparent!important}

img[src*="http://assets.tumblr.com/images/auto_pagination_loader.gif"]{
display:none!important}

.new_post_label/*_icon*/ > I {
  background-image: url('http://assets.tumblr.com/images/new_post_icons_no_labels.png') !important;
  }
.new_post {background-color: transparent !important}

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 2px -5px !important}
#new_post_label_photo > I {background-position: -73px -5px !important}
#new_post_label_quote > I {background-position: -148px -5px !important}
#new_post_label_link > I {background-position: -223px -5px  !important}
#new_post_label_chat > I {background-position: -298px -5px !important}
#new_post_label_audio > I {background-position: -373px -5px !important}
#new_post_label_video > I {background-position: -448px -5px  !important}

#new_post_label_text:hover > I {background-position: 2px -9px !important}
#new_post_label_photo:hover > I {background-position: -73px -9px !important}
#new_post_label_quote:hover > I {background-position: -148px -9px !important}
#new_post_label_link:hover > I {background-position: -223px -9px  !important}
#new_post_label_chat:hover > I {background-position: -298px -9px !important}
#new_post_label_audio:hover > I {background-position: -373px -9px  !important}
#new_post_label_video:hover > I {background-position: -448px -9px  !important}

#new_post_label_text:active > I {background-position: 2px -5px !important}
#new_post_label_photo:active > I {background-position: -73px -5px !important}
#new_post_label_quote:active > I {background-position: -148px -5px !important}
#new_post_label_link:active > I {background-position: -223px -5px !important}
#new_post_label_chat:active > I {background-position: -298px -5px !important}
#new_post_label_audio:active > I {background-position: -373px -5px !important}
#new_post_label_video:active > I {background-position: -448px -5px !important}


.customize_button {
background: transparent !important;
border:0px!important;}

.left_column {
background: transparent !important;
border:0px!important;}

.right_column {
background: transparent !important;
border:0px!important;}

.tag_page_header{
background-color:transparent!important}

#auto_pagination_loader {
background: none !important;
border-top:0px!important;}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 1px !important;
border-color: #000000 !important;}

ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #000000 !important;}

#posts ol.notes li {background:transparent!important;}

#posts ol.notes li.answer {
background:transparent!important;}

.right_column_section .selected{
/*--background:transparent!imporant;--*/
background:-moz-linear-gradient(top,#84ac54 0,#7ca44b 50%,#739946 50%,#6b903e 100%);
background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#84ac54),color-stop(50%,#7ca44b),color-stop(50%,#739946),color-stop(100%,#6b903e));
/*--filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#84AC54',endColorstr='#6B903E',GradientType=0);--*/
-webkit-box-shadow:none!important;
box-shadow:none!important;
border-radius:1px;
border-top:none}
}
