css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk") {

body {
    font-family: Palatino Linotype, Corbel, Candara;
    background-color: #080808 !important;
    color: #678 !important;
}

[method='post'] {
    width: 950px !important;
    margin: auto !important;
    background-color: #0c0c0c !important;
}

.reply, .highlight, .de-pview {    
    background-color: #151515 !important;
    border: 1px solid #222 !important;
    border-radius: 7px;
    padding-bottom: 5px !important;
    color: #678 !important;
}
 
.reply:not(.de-pview):not(div), .highlight:not(.de-pview) {
    width: 850px !important;
}

.highlight {
    box-shadow: -3px -5px 1em rgba(99,77,77,.8) inset;
    border-width: 3px !important;
}

.reply .replytitle {
    color: #774 !important;
}

.omittedposts, [method='post'] table {
    clear: both !important;
    display: block !important;
}

.omittedposts {
    margin: 10px 50px !important;
    font-size: 11pt !important;
    color: #4f4f4f !important;
    text-shadow: 0 0 15px rgba(255,255,255,.3) !important;
}

.doubledash {
    visibility: hidden !important;
    margin-right: 20px !important;
}

label .filetitle {
    color: #467 !important;
    background: -moz-linear-gradient(top, rgb(50,50,50), rgb(40,40,40), rgb(30,30,30), rgb(10,10,10)) !important;
    background: -webkit-linear-gradient(top, rgb(50,50,50), rgb(40,40,40), rgb(30,30,30), rgb(10,10,10)) !important;
    background: -o-linear-gradient(top, rgb(50,50,50), rgb(40,40,40), rgb(30,30,30), rgb(10,10,10)) !important;
    background: linear-gradient(top, rgb(50,50,50), rgb(40,40,40), rgb(30,30,30), rgb(10,10,10)) !important;
    border-top: 1px solid #444;
    border-right: 1px solid #303030;
    border-left: 1px solid #2b2b2b;
    border-bottom: 1px solid #202020;
}

.postername, .commentpostername, #de-qarea-target {
    color: #234 !important;
    text-shadow: 0 1px 0 #555;
}

.de-refmap::before {
    content: 'hui' !important;
    visibility: hidden !important;
}

[method='post']  {
    color: #678 !important;
    font-size: 13pt !important;
}

[method='post'] a:not(.de-imgmenu):not([target='_blank']), .de-reflink, a[rel='nofollow'], blockquote a,
.reflink a {
    background-image: -moz-linear-gradient(top center, #444, #444, #333, #111, #222, #222, #222) !important;
    background-image: -webkit-linear-gradient(top center, #444, #444, #333, #111, #222, #222, #222) !important;
    background-image: -o-linear-gradient(top center, #444, #444, #333, #111, #222, #222, #222) !important;
    background-image: linear-gradient(top center, #444, #444, #333, #111, #222, #222, #222) !important;
    background-color: #262626 !important;
    border: 1px solid #303030;
    text-decoration: none !important;
    padding: 0 3px !important;
    transition: box-shadow 0.333s;
    color: #666 !important;
}

[method='post'] a:not(.de-imgmenu):not([target='_blank']):hover, .de-reflink:hover, a[rel='nofollow']:hover, blockquote a:hover, .reflink a:hover  {
    background: -moz-radial-gradient(center bottom, rgba(247,173,102,.5),transparent),-moz-linear-gradient(top,hsl(0,0%,33%) 48%,hsl(0,0%,20%) 49%,hsl(0,0%,0%) 85%,hsl(0,0%,20%)) !important;
    background: -webkit-radial-gradient(center bottom, rgba(247,173,102,.5),transparent),-webkit-linear-gradient(top,hsl(0,0%,33%) 48%,hsl(0,0%,20%) 49%,hsl(0,0%,0%) 85%,hsl(0,0%,20%)) !important;
    background: -o-radial-gradient(center bottom, rgba(247,173,102,.5),transparent),-o-linear-gradient(top,hsl(0,0%,33%) 48%,hsl(0,0%,20%) 49%,hsl(0,0%,0%) 85%,hsl(0,0%,20%)) !important;
    background: radial-gradient(center bottom, rgba(247,173,102,.5),transparent),linear-gradient(top,hsl(0,0%,33%) 48%,hsl(0,0%,20%) 49%,hsl(0,0%,0%) 85%,hsl(0,0%,20%)) !important;
    color: #a9a778 !important;
    box-shadow: 0 0 5px #a9a767;
}


.list {
    background-color: #202020 !important;
    border: none !important;
}

.header {
    background: -moz-linear-gradient(top, #555, #444, #333) !important;
    background: -webkit-linear-gradient(top, #555, #444, #333) !important;
    background: -o-linear-gradient(top, #555, #444, #333) !important;
    background: linear-gradient(top, #555, #444, #333) !important;
    color: #898069 !important;
    padding-top: 4px!important;
    padding-bottom: 4px !important;
    text-align: center !important;
    border: 1px solid black; 
    border-radius: 4px;
}

.category {
    background-color: black !important;
}

*::-moz-selection {
    color: #ba7;
}

div [target='board'] {
    font-size: 1.1em !important;
}

div [target='board']:hover, .logo a:hover, .logo:hover, .sublogo a:hover {
    color: #ba7 !important;
    text-shadow: 0 0 1em rgba(255,255,0,.6) !important;
}

.logo, .sublogo, .logo a, .sublogo a {
    color: #404040 !important;
    text-decoration: none !important;
    text-shadow: 1px 1px 0 rgba(255,255,255,.4);
}

.logo a {
    font-size: 1.1em !important;
}


.spoiler, .spoiler a {
    background-color: #343434 !important;
    color: #343434 !important;
    transition: all 0.666s;
}

.spoiler:hover {
    background-color: inherit !important;
    color: inherit !important;
    border-bottom: 1px dashed #456767;
    opacity: 0.8;
}

a[target='_blank'] {
    border: none !important;
}

.unkfunc {
    font-style: italic !important;
    color: #776 !important;
}

.postblock {
    background-color: #101003 !important;
    border: 1px solid #332 !important;
    color: #665 !important;
}



.filesize a{
    background: none !important;
    border: 1px solid #444;
    text-decoration: none !important;
    color: #654 !important;
    border-radius: 4px;
}

.filesize a:hover {    
    background: none !important;
    box-shadow: 0 0 4px #aa8;
    color: #987 !important;
}

form div label:not(input):not(span), .reply label:not(input):not(span) {
    color: #333 !important;
}

.filesize {
    color: #775 !important;
    opacity: 0.6;
}

.thumbnailmsg {
    display: none !important;
}

.list a {
    color: #679 !important;
}

.list a:hover {
    color: #997 !important;
    background: none !important;
}

[method='post'] div label:not(.de-block) {
    margin-left: -8px !important;
    background: #111 !important;
    padding: 1px 0 !important;
}

[method='post'] .reply label, .highlight label {
    margin-left: -9px !important;
    background-color: #1a1a1a !important;
    padding: 1px 0 !important;
    border-radius: 7px 0 0 0;
}

.adminbar {
    color: rgba(0,0,0,0) !important;
    background: -moz-linear-gradient(top, #344, #233, #112, #011, #000) !important;
    background: -webkit-linear-gradient(top, #344, #233, #112, #011, #000) !important;
    background: -o-linear-gradient(top, #344, #233, #112, #011, #000) !important;
    background: linear-gradient(top, #344, #233, #112, #011, #000) !important;
    width: 100% !important;
    text-align: center !important;
}

.adminbar a {
    border-radius: 5px;
    margin-top: 10px !important;
    text-decoration: none !important;
    color: #121212 !important;
    text-shadow: 0 1px #666, 1px 0 #909090, -1px 0 #888, 0 -1px #666;
    opacity: 0.8;
    font-size: 0.9em;
    padding: 1px 3px !important;
    line-height: 26px;
}

.adminbar a:hover {
    color: #89a !important;
    text-shadow: 0 1px 0 rgba(255,255,0,.4);
    opacity: 1;
}

.greeting {
    color: #707070 !important;
    font-style: italic !important;
}

.greeting a {
    color: #9ab !important;
    text-decoration: none !important;
}

.greeting a:hover {
    color: #c97 !important;
}

.news {
    visibility: hidden !important;
}

table[border='1'] {
    border-left: 1px solid #578 !important;
    border-bottom: 1px solid #345 !important;
    border-right: 1px solid #234 !important;
    border-top: 1px solid #789 !important;
    width: 950px !important;
    margin: auto !important;
}


table[border='1'] tr td {
    color: #440 !important;
    background-color: #232323 !important;
    border: none !important;
}

table[border='1'] a, a[href*='/index.html'] {
    color: #887 !important;
    text-decoration: none !important;
}

table[border='1'] a:hover {
    color: #993 !important;
    text-shadow: 0 0 5px #993;
}

.footer {
    color: whitesmoke !important;
}

.footer a {
    text-decoration: none !important;
    color: #333 !important;
}

.footer a:hover{
    color: #999 !important;
}

.nothumb a {
    visibility: hidden !important;
}

.nothumb {
    background: url('http://i.imgur.com/yk5XdVg.png') no-repeat scroll #fff !important;
    background-size: 150px 150px !important;
    width: 150px !important;
    height: 150px !important;
    padding: 0 !important;
}

.userdelete {
    border: 1px solid #444;
    border-radius: 8px;
    background-color: #101010 !important;
    width: 370px !important;   
    transition: all 0.666s;
    position: fixed !important;
    left: 100% !important;
    bottom: 30px !important;
    margin-left: -30px !important;
    padding-left: 30px !important;
    padding-top: 10px !important;
    z-index: 666 !important;
}

.userdelete:hover {
    margin-left: -370px !important;
}

textarea, input[type='text'] {
    background-color: #0c0c0c !important;
    -moz-appearance: none !important;
    color: #bb9 !important;
    border: 1px solid #774 !important;
    padding: 2px 3px !important;
}


textarea:focus, input[type='text']:focus {
    box-shadow: 0 0 4px 1px rgba(255,255,155,.3) inset !important;
    color: #abc !important;
}

input[type='submit']{
    background-color: inherit !important;
    -moz-appearance: none !important;
    border: 1px solid #444 !important;
    border-radius: 8px 8px 4px 4px;
    color: #cfdf88 !important;
    cursor: pointer !important;
}

    
input[type='submit']:hover {
    color: #f00000 !important;
    text-shadow: 0 1em 1em #ff5, 1em 0 1em #ff5 !important;
    border-color: #acad70 !important;
}


.de-menu {
    color: #ca7 !important;
    border: 1px solid #333 !important;
}

.de-menu span:hover,  .de-menu a:hover{
    color: #fe9 !important;
    background-color: inherit !important;
}

#de-qarea {
    opacity: 0.9 !important;
    width: 700px !important;
}

.de-cfg-body {
    margin-left: 10px !important;
    border: none !important;
}

.de-abtn, #de-cfg-info a {
    color: #565656 !important;
    text-decoration: none !important;
}

.de-abtn:hover, #de-cfg-info a:hover {
    color: #884 !important;
    text-shadow: 0 0 1em #ff0 !important;
}

#de-parea-up:not(#postform), #de-parea-down:not(#postform) {
    color: black !important;
}

.de-parea div a {
    color: #012 !important;
    border: 1px solid #666;
    border-radius: 5px;
    background-color: #333 !important;
    padding: 0 3px;
    transition: all 0.7s;
}

.de-parea div a:hover {
    background-color: #444 !important;
    color: #a94 !important;
}

#de-parea-down {
    margin: auto !important;
    width: 950px !important;
}

.de-cfg-body label:hover {
    color: #666 !important;
}

}
