css:
@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 url("krautchan.net"),
 url-prefix(http://krautchan.net),
 url-prefix(https://krautchan.net),
 domain("www.krautchan.net") {


/*
body > div {
position: fixed;
margin-left: 8%;
padding: 2px;
}
*/

/*
form#postform img[src*="button-expand.gif"] {
 display: none !important; }
 */

div#WzTtDiV, form[action="/paint"] {
 display: none !important; }

img[src*="/banner/"] {
 width: 600px !important;
 height: 200px !important; }

img[src*="icon-report.png"], img[src*="button-paint.gif"], img[src*="button-question.gif"] {
 display: none !important; }

img[src*="button-expand.gif"] {
 width: 35px !important;
 height: 35px !important; }

img[src*="button-close.gif"], img[src*="button-open.gif"] {
 width: 47px !important;
 height: 47px !important; }
 
 img[src*="button-open.gif"] {
 margin-left: 30px !important; }
 
 img[src*="button-open.gif"] {
 margin-bottom: -13px !important; }
 
 span.hidden_thread {
  font-size: 2.4em !important;
  margin-left: 10px !important; }

/* Navi oben */
    div[style="float: left"] {
     font-size: 4.0em !important;
     color: rgb(49, 51, 112) !important;
      }
     
    div[style="float: left"] :link, div[style="float: left"] :visited {
     background-color: rgb(49, 51, 112) !important;

     font-size: 0.9em !important;
     font-weight: bold !important;
     padding: 0 30px 0 30px !important;
     margin: 0px !important;
   color: #fff !important;
 text-decoration: none !important; }  
     
    div[style="float: left"] :focus {
     background-color: rgb(170, 170, 204) !important;
     border: solid 1px #fff !important; }  


    div[style="float: right"] {
    color: rgb(34, 34, 153) !important;
     font-size: 2.9em !important;
     margin-top: -1.15em !important;}

    div[style="float: right"] a {
    color: rgb(34, 34, 153) !important;
     font-size: 1.0em !important;
     margin-top: -1.15em !important; }
     
/* Navi unten */            
     body a[onmouseout="UnTip()"] {
     font-size: 3.5em !important;
     font-weight: bold !important;
     padding: 0 22px 0 22px !important;
     margin: 0 -10px 0 -10px !important;}
     
     table a[onmouseout="UnTip()"] {
     font-size: 0em !important; }
     
     .file_thread a[onmouseout="UnTip()"] {
     font-size: 0em !important; }
     
    div[style="position: absolute; left: 5px;"] {
    font-size: 3.4em !important;
    margin-top: 330px !important; }

    div[style="position: absolute; right: 5px;"] {
    font-size: 2.4em !important; }

/* Formular */
    textarea#postform_comment {
    width: 650px; height: 200px;
    font-size: 1.4em !important; }
    
    input {
    font-size: 1.1em !important; }
    
    form#postform {
    font-size: 2.4em !important; }
      
/* input buttons */
    input[type=checkbox], input[type=radio] {
    -moz-appearance: none !important;
    width: 35px !important;
    height: 35px !important; }
      
    div[style="margin-top: 5px"] {
    display: none !important; }
      
      
    
/* brett-name */
    h1 {
     font-size: 4.4em !important; }
    h2, body center p {
     font-size: 3.4em !important; }


/* posts */ 
    .thread_body {
     font-size: 2.4em !important; }
  
     .postheader {
     font-size: 1.2em !important;
     font-weight: bold !important; } 
     
    img[src*="/balls/"] {
     font-size: 0.9em !important;
     width: 37px !important;
     height: 34px !important;} 
     

/* Antworten */
     .postheader a {
     font-size: 0.9em !important;
     font-weight: bold !important; }  
     
     span.postername {
     font-size: 0.8em !important;
     font-weight: bold !important; }  
 
 /* Post-ID */ 
     .postheader a.quotelink {
      font-size: 0.8em !important;
      font-weight: normal !important; }
      
     span.sage {
      font-size: 0.7em !important;
      font-weight: bold !important; }

 /* Post-ID */ 
     .postheader span.postdate {
      font-size: 0.8em !important;
      font-weight: normal !important; }
 
   
/* post titel */ 
    .postsubject {
     font-size: 0.9em !important; }
 
 
 /* infos bild */ 
    .fileinfo {
     font-size: 0.6em !important; }
 
 

 /* Bilder vergrößern */
 

.file_thread, .file_reply {
  min-width: 350px !important; }
 

a img[src*="/thumbnails/"] {
  width: 100% !important;
  height: 100% !important;
  max-height: 500px !important; }

.thread_body div:first-child + div + div + div, .postreply div:first-child + div + div + div {
  clear: both !important; }


 
 
 
 }
