css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("speedrunslive.com")
{

    #pageContent
    {
        width: 98% !important;
        height: 100% !important;
        display: block !important;
    }
    
    #featured
    {
        position:absolute !important;
        height: 73% !important;
        width: 98% !important;
        display: flex !important;
    }
    
    .featuredstreamcontainer
    {
        position:relative;
        margin-right: 15px !important;
        width: 80% !important;
        height: 100% !important;
        float: left !important;
    }
    
    .featuredstream
    {
        height: 100% !important;
    }
    
    #live_embed_player_flash
    {
        width: 100% !important;
        height: 100% !important;
        position: absolute !important;
    }
    
    .hitboxframe
    {
        min-height: 350px !important;
        height: 100% !important;
    }
    
    .featuredchat
    {
        position: relative !important;
        top: 40px !important;
    }
    
    .featuredchat iframe
    {
        height:103% !important;
        min-height: 350px !important;
    }
    
    #streamList
    {
        position: absolute !important;
        top: 100% !important;
    }
    
    .twitchstreamer
    {
        margin-right: 15px !important;
        width: 31.5% !important;
    }

    #racesNav
    {
        width: 20% !important;
        margin-left: 5% !important;
    }
    
    #racesNav > a
    {
        width: 100% !important;
    }

    #racesMain
    {
        margin-left: 2% !important;
        float: left !important;
        width: 64% !important;
    }
    
    .racebar
    {
        width:100% !important;
        display: flex !important;
    }
    
    .racebar .left
    {
        width: 70% !important;
    }

    #channel_info
    {
        width: 15% !important;
        margin-right: 2% !important;
    }

    #mibbit_embed
    {
        width: 82% !important;
        height: 705px !important;
        margin-right: 0px !important;
    }

    #faqNav
    {
        margin-right: 5% !important;
        width: 17% !important;
    }
    
    #faq
    {
        width: 65% !important;
    }
    
    #tools_left
    {
        width: 40% !important;
        padding-right: 5% !important;
    }

    #tools_right
    {
        width: 40% !important;
        float: left !important;
    }
    
    footer.defaultborder
    {
        position: absolute !important;
        bottom: -2000px !important;
    }

}
