/* Purple and Yellow blocks */
.homepage-yellow-block, .purpleBlock.alignC {
  background: #333 !important; padding: 8px 10px !important;
  border: 1px solid #444 !important;
  /*-moz-*/border-radius: 10px !important;
  }
.homepage-yellow-block-bot, .purpleBlockBot { display: none !important; }


