#button-signin-title, .action-button-text, .edit-button-text, .title, .search-button-text { color: Highlight !important; text-decoration: none !important; }
.watch-action-text { text-decoration: none !important; }
#button-signin-title:hover, .action-button-text:hover, .watch-action-text:hover, .watch-playlist-action:hover, .edit-button-text:hover, .title:hover, .search-button-text:hover { color: #ff6600 !important; }

/* login box */
