.signup-outer-frame, #loginBoxZ { background: none !important; border: none !important; }
.signInBoxBorder, .signup-inner-frame, .contentBox, #loginBoxZ > DIV  { background: #282828 !important; border-color: #444 !important; }
.signInBoxContent { background: #333 !important; border-color: #444 !important; }
