css:
@-moz-document domain("forum-3dcenter.org") {
@media all{
  body {
    min-width:800px !important;
  }
  #header-primary-tabs {
    position: absolute;
    right: 15px;
    background: none !important;
    display: none;
  }
  #header-primary-tabs, #header-primary-tabs * {
    background: none !important;
    color: #000 !important;
  }
  #header-primary-tabs .start, #header-primary-tabs .accentuated-end {
    display: none;
  }
  #header-primary-tabs ul li a {
    width: auto !important;
    padding: 15px 20px !important;
  }
  .bg-container-right,
  .bg-container-left,
  .bg-header,
  .bg-sidebar-right-top,
  .bg-sidebar-left-top,
  .bg-container-bottom,
  .bg-container-bottom-right,
  .bg-container-bottom-left {
    background:transparent !important;
  }
  #container {
    margin:7px !important;
    padding:0 !important;
    box-shadow:0 0 5px #888;
    border-radius:10px 10px 0 0;
  }
  #header {
    height:110px !important;
    background:url('https://www.forum-3dcenter.org/vbulletin/images/3dc/style-images/bg-header.png') !important;
    border-radius:10px 10px 0 0;
  }
}
@media all and (max-width: 960px) {
  #container {
    margin:0 !important;
  }
  #container, #header {
    border-radius:0 !important;
  }
  .pagenav .thead {
    display:none;
  }
}
}
