@-moz-document domain("androidcentral.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.emailform .submit__button,
.flexi-page.responsive.flexi-home .widget-title,
.list-title,
.popular-box__label__tab,
.vDXZS,
.widget-dynamic.tag-blocks-display .widget-title,
.widget-dynamic.tag-blocks-display .tabs-content .wel-item-title
{
	text-transform: capitalize!important;
}

.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(2), .feature-block.top-featured .feature-block-item-wrapper:nth-of-type(3),
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(1),
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
__________________________________________________________________________________________________________________________________*/


.flexi-page.responsive.flexi-home .widget-title
{
	font-size: 3rem!important;
}

.listingResult .article-name,
.list-title
{
	font-size: 1.85rem!important;
}

.listingResult .synopsis
{
	font-size: 1.2rem!important;
}

/* Remove Items 
__________________________________________________________________________________________________________________________________*/

.widget-empty,

body:not(.no-ads) .dfp-leaderboard-container,

[class*='widget-ads'],
[id*='-ad-']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Margins 
__________________________________________________________________________________________________________________________________*/


#main
{
	margin: 1.2rem!important;
}

/* Paddings 
__________________________________________________________________________________________________________________________________*/


.widget-dynamic.tag-blocks-display .wel-item
{
	padding: 13px 26px!important;
}

/* Widths 
__________________________________________________________________________________________________________________________________*/


.flexi-page.responsive #main.container.full
{
	max-width: none!important;
}

.flexi-page.responsive .widget-area-group-2col #widgetArea16
{
	width: 86%!important;
}

}

