@-moz-document domain("androidcentral.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
__________________________________________________________________________________________________________________________________*/


.popular-box__article-list:hover .popular-box__article-list__number-text,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
__________________________________________________________________________________________________________________________________*/

.flexi-page .widget-dynamic-carousel-rounded.widget .whc-carousel .whc-item-dek,
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
__________________________________________________________________________________________________________________________________*/

.popular-box__label__tab--active,
.flexi-page.responsive.flexi-home .widget-title,
.vDXZS,
.newsletter-form__header,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
__________________________________________________________________________________________________________________________________*/


h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
__________________________________________________________________________________________________________________________________*/


h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
__________________________________________________________________________________________________________________________________*/


time
 {
	color: #07beff!important;
}

/* Green 
__________________________________________________________________________________________________________________________________*/


selector
 {
	color: #07ff24!important;
}

/* Red 
__________________________________________________________________________________________________________________________________*/


selector
 {
	color: #ff0707!important;
}

/*
__________________________________________________________________________________________________________________________________*/


input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
__________________________________________________________________________________________________________________________________*/


::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
__________________________________________________________________________________________________________________________________*/


selector
{
	color: #fff!important;
}


}

@-moz-document domain("androidcentral.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
__________________________________________________________________________________________________________________________________*/


#document-footer
{
	background: #060505!important;
	background-color: #060505!important;
}

.ftDECR
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
__________________________________________________________________________________________________________________________________*/

.feature-block.top-featured,
.list-text-links .listingResult .content,
.list-text-links .listingResult,
.listingResult,
.newsletter-signup,
.popular-box,
.widget-newsletter.widget-newsletter-fter,

body .top-featured .feature-block-item, body .feature-block.top-featured .feature-block-item-wrapper
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
__________________________________________________________________________________________________________________________________*/

/* Blue 
__________________________________________________________________________________________________________________________________*/


.widget-dynamic .more-link
{
	background: linear-gradient(45deg, #012654 0%, #01748b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
	padding: .5rem!important;
}

.widget-dynamic .more-link:hover
{
	background: linear-gradient(135deg, #01748b 0%, #012654 100%)!important;
	color: #ff0!important;
}

/* Red 
__________________________________________________________________________________________________________________________________*/

.emailform .submit__button,
.newsletter-form__form input[type=submit]
{
	background: linear-gradient(45deg, #540101 0%, #c51111 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	padding: .5rem!important;
}

.emailform .submit__button:hover,
.newsletter-form__form input[type=submit]:hover
{
	background: linear-gradient(135deg, #c51111 0%, #540101 100%)!important;
	color: #ff0!important;
}

/* Plain Black 
__________________________________________________________________________________________________________________________________*/


#main
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
__________________________________________________________________________________________________________________________________*/


.newsletter-form__wrapper
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#main:hover,
.newsletter-form__wrapper:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Gray Gradient 
__________________________________________________________________________________________________________________________________*/

.popular-box__label__tab--active,
.newsletter-signup>.separator-heading,
.burgerbar
{
	background: linear-gradient(to bottom, #333 0%, #000 100%)!important;
}

/* Green Gradient 
__________________________________________________________________________________________________________________________________*/


.primary-nav .wrapper
{
	background: linear-gradient(to bottom, #1ccc3e 0%, #000 100%)!important;
}

/* Radial Background 
__________________________________________________________________________________________________________________________________*/

.widget-dynamic.tag-blocks-display .wel-item:hover
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("androidcentral.com") {
/* Image Settings */

.impact .listingResult,
.list-text-links .listingResult,
.listingResult,
.popular-box__article-list,

body .feature-block.top-featured .feature-block-item-wrapper,
body .top-featured .feature-block-item
 {
	filter: brightness(75%)!important;
}

/*
__________________________________________________________________________________________________________________________________*/

.impact .listingResult:hover,
.list-text-links .listingResult:hover,
.listingResult:hover,
.popular-box__article-list:hover,

body .feature-block.top-featured .feature-block-item-wrapper:hover,
body .top-featured .feature-block-item:hover
 {
	filter: brightness(100%)!important;
}

/*
__________________________________________________________________________________________________________________________________*/


img
 {
	border-radius: 22px!important;
}

/*
__________________________________________________________________________________________________________________________________*/


img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("androidcentral.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

selector
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

selector
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Checkmark _______________________________________________________________________________*/

label {
    line-height: 2.4;
}
/* Big Check ___________________________*/

/* Checked ___________________________*/

selector
{
	background: url(https://i.ibb.co/ZMNmHwc/Mcrw-IRxnnrq.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

selector
{
	background: url(https://i.ibb.co/thJkp9V/big-check-off.png) no-repeat!important;
	box-sizing: border-box;
}

selector {
    background-size: 31px!important;
    height: 32px!important;
    padding-left: 32px!important;
    width: 1px!important;
}

/* Checked ___________________________*/

.emailform .emailform__checkbox-row .emailform__checkbox-row__checkbox:checked,
.newsletter-form__form input[type=checkbox]:checked
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

.emailform .emailform__checkbox-row .emailform__checkbox-row__checkbox,
.newsletter-form__form input[type=checkbox]
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

.emailform .emailform__checkbox-row .emailform__checkbox-row__checkbox:checked,
.emailform .emailform__checkbox-row .emailform__checkbox-row__checkbox,
.newsletter-form__form input[type=checkbox]:checked,
.newsletter-form__form input[type=checkbox]
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Close (X) ___________________________________________________________________________*/

.exit-intent__close-button svg
{
	background: url(https://i.ibb.co/kxQCnBQ/close-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

.exit-intent__close-button svg
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}


}

@-moz-document domain("androidcentral.com") {
/* Scrollbar Settings */

/* Width 
__________________________________________________________________________________________________________________________________*/

 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
__________________________________________________________________________________________________________________________________*/


::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
