css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thetruthaboutcars.com") {


@font-face {
  font-family: 'Faster One';
  font-style: normal;
  font-weight: 400;
  src: local('Faster One'), local('FasterOne-Regular'), url(https://themes.googleusercontent.com/static/fonts/fasterone/v1/R45a6_a3GGq28ugU8uxGWb3hpw3pgy2gAi-Ip7WPMi0.woff) format('woff');
    }

#posttitle h1, #side_content .categories h3, #searchBar strong, #pageTitle h1.category, #pageTitle h1   {font-family: 'Faster One' !important;}
#searchBar strong {font-size: 1.2em !important;}
.logo, .newsletter, #newsletter, .recent_comments, .tborder,
.new_specs, .forabg, .facebook, #footer, #post_footer div.text_wrapper,
#postcomments  h2, #related_posts, .solo-subscribe-to-comments, .staff {
    display: none !important;
    }
.forumnetwork, .navigation {
    margin: 0 !important;
    }
.forumnetwork {padding: 0 !important;}
.forumnetwork ul {margin-bottom: 8px !important;}
.forumnetwork ul.links li a {padding: 4px !important;}
.container   { font-size: 1em !important;}
.postcontainer #posttitle h1 {font-size: 1.75em !important;}
.postbody p {font-size: 1.25em !important; line-height: 1.2 !important; color: #454545 !important;}
#side_content table.thumbs a {font-size: 0.75em !important;}

#side_content table.thumbs a, #side_content ul.articles li a, a, #posttitle h1 a,
#header div.features a  {
    -webkit-transition: all 0.25s ease-in-out 0s !important;
    -moz-transition:  all 0.25s ease-in-out 0s !important;
    -o-transition: all 0.25s ease-in-out 0s !important;
    transition: all 0.25s ease-in-out 0s !important;
    }
#side_content ul.articles li a:hover, #posttitle h1 a:hover {color: rgb(158, 26, 25) !important;}
a:hover {border-bottom: 3px dotted rgb(158, 26, 25) !important; text-decoration: none !important;}
#header div.navigation li a:hover, a.more-link:hover, .forumnetwork ul.icons a.twitter:hover, .forumnetwork ul.icons a.rss:hover { border-bottom: none !important;}
}
