css:
toolbar:not(#TabsToolbar):not(#toolbar-menubar):not(.devtools-toolbar):not(.devtools-tabbar)  {
  background: #F5F6F7 !important;
}
#main-window[tabsintitlebar]:not([inFullscreen]) #toolbar-menubar[inactive] ~ #TabsToolbar:not(:-moz-lwtheme) {
	background: linear-gradient(to top, rgba(10%,10%,10%,.25) 1px, Transparent 1px) !important}

@-moz-document url("about:addons") {

#addons-page {
  background: #E9E9E9 !important;
}

}

.tabbrowser-tab:not([selected="true"]),
.tabs-newtab-button {
  background: linear-gradient(to top, rgba(10%,10%,10%,.4) 0px, transparent 1px), 
              #D7D7D7 !important;
  background-origin: border-box !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important;
  background-size: calc(100% - 2px) calc(100% - 2px) !important;
}

.tabbrowser-tab:not([selected="true"]):hover,
.tabs-newtab-button:hover {
  background: linear-gradient(to top, rgba(10%,10%,10%,.4) 1px, transparent 1px),
              #EBEBEB !important;
  background-origin: border-box !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important;
  background-size: calc(100% - 2px) calc(100% - 2px) !important;
}

.tabbrowser-tab[selected="true"] {          
  background: linear-gradient(#F5F6F7, #F5F6F7) !important;
  background-origin: border-box !important;
  background-repeat: no-repeat !important;
  background-position: 1px 2px !important;
  background-size: calc(100% - 2px) calc(100% - 2px) !important;
}

.tab-close-button:hover,
.tab-close-button:hover[selected="true"],
.tabs-closebutton:hover,
#addonbar-closebutton:hover,
.findbar-closebutton:hover {
  -moz-image-region: rect(0, 48px, 16px, 32px) !important;
}

#GBL-Toolbar, #PersonalToolbar {
  border-top : 2px groove #fff !important;
}

.tabs-newtab-button,
.tabbrowser-tab{
    -moz-border-image: none !important;
    border-style: solid !important;
    border-color: #AAAAAA !important;
    border-width: 1px 1px 0 1px !important;
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    padding: 4px 4px 5px !important;
}
.tabs-newtab-button,.tabs-newtab-button:hover{
    background: transparent !important;
    border-color: transparent !important;
}

/*Stop, Go, Reload*/
#urlbar-go-button,
#urlbar-reload-button,
#urlbar-stop-button{
border-left: 1px solid rgb(217,217,217) !important;
padding: 3px 4px 4px 6px !important;
}

#appmenu-button .button-menu-dropmarker {
	list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAAO0lEQVR4AWMgGnz//v3o379//6Dj379/30dR+OfPn0//kcDPnz9vYzURqPsHSAFIA16rf/369YyBZgAAx2Y+f6eC6EYAAAAASUVORK5CYII=") !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button > .button-box > .box-inherit > .button-icon {
	list-style-image: url("data:image/png;base64,") !important}
#appmenu-button {
	background: #E78D00 !important; /* Mine2 */
	border-radius: 0 !important;
	border: 0 !important;
	box-shadow: none !important;
	text-shadow: none !important;
        max-width: 30px;
        max-height: 15px;

}
#main-window:-moz-window-inactive #appmenu-button:not(:hover) {
	background: rgba(0,0,0,.2) !important}
#appmenu-button:hover:not(:active):not([open]) {
	background: #F1BB66 !important; /* Mine */
	transition: .3s ease-in !important}
#appmenu-button:hover:active,
#appmenu-button[open] {
	background: #BA5C12 !important; /* Mine */
	-moz-transition: .2s ease-out !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button {
	background: #6F11B2 !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button:hover:not(:active):not([open]) {
	background: #832FBF !important}
#main-window[privatebrowsingmode=temporary] #appmenu-button:hover:active,
#main-window[privatebrowsingmode=temporary] #appmenu-button[open] {
	background: #5D0999 !important}
#main-window[sizemode=maximized] #appmenu-button-container {
	-moz-margin-start: 0px !important}



#appmenu-button .button-box .box-inherit:before 
{
    content: "Firefox";
    margin: 0px 0px 0px -7px;

}

#appmenu-button .button-box .box-inherit .button-text 
{
    display: none;
} 

.urlbar-input-box{
  font-size: 14px !important;
}

.tab-throbber[progress], .tab-throbber[busy]
{
	list-style-image: url("data:image/png;base64,") !important;
}
