css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.phoronix.com") {

body {
    font-family: monospace !important;
}

#phxcms_content_phx {
    font-family: monospace !important;
    min-height: 900px !important;
}

#phxcms_content_phx .phxcms_normal_format {
    font-size: 14px !important;
}

#phxcms_content_left a {
    padding: 3px !important;
    width: auto !important;
    font-size: 12px !important;
}

#phxcms_content_left .phxcms_nav_int ~ ol li:first-child {
    background-color: #000000 !important;
    font-weight: bold !important;
    padding: 1px !important;
}

#phxcms_right_menu ol {
    margin-bottom: 20px !important;
}

.phxcms_copyright > div > div:first-child {
    padding: 0 20px 20px;
}

.phxcms_copyright > div > div:last-child {
    padding: 0 10px 10px;
    margin-top: -10px;
}

}
