css:
@-moz-document url-prefix(http://www.tumblr.com/dashboard), url-prefix(https://www.tumblr.com/dashboard) {

/* left col */
.l-container.l-container--two-column .l-content, 
.l-container.l-container--two-column-dashboard .l-content {
  background-color:transparent;
}

/* right col */
.right_column { 
  display:none; 
}
.l-container.l-container--two-column .right_column, 
.l-container.l-container--two-column-dashboard .right_column {
  background-color:transparent;
}

/* remove rhs blue gradients from sidebar items */
.identity .controls_section.user_list li:hover .follow_list_item_blog:before,
.identity .controls_section.user_list li .follow_list_item_blog:before,
.radar .radar_footer .radar_avatar:before,
.identity .right_column:after {
  background:none;
  background-image:none;
  border-color:none;
}

/* re-apply backgrounds in neue style */
body,
.l-header-container, 
.l-header-container--refresh { 
  background:#333 url(https://i.imgur.com/d54UGPl.png) left top repeat !important; 
}

/* compose button */
.compose-button {
  border-color:#ccc;
  fill:#ccc;
}
.compose-button:hover {
  border-color:#fff;
  fill:#fff;
}

.l-container.l-container--two-column-dashboard .left_column {
  background-color:transparent;
}

/* new prev/next */
.chrome.blue,
.chrome.blue:active {
  border-color:#9da6af;
  background:#9da6af;
}

/* selection nipple */
.identity-refresh .post_full .post_permalink {
  border-color:#333 #333 transparent transparent;
}

/* hide new testing rapid-recs feature */
.rapid-recs-container {
  display:none !important;
}

}
