css:
@-moz-document url-prefix("http://www.fkzww.com/") {
div#bdshare,
[id^="cproIframe"],
div.footer,
span#readtools,
div#content p a, /*正文中广告链接*/
div.con_top,
div#ad_headerbanner,
div[id^="BAIDU_DUP_wrapper_"],
div.menu_navcc
{
display:none !important;
}

.content_read {
    overflow: inherit!important;
}

#content p {
font-size: 16px;
font-family: "Microsoft YaHei","微软雅黑","楷体","楷体_GB2312","宋体";
padding-top: 15px;
line-height: 150%;
}
}



