css:
@-moz-document url("chrome://browser/content/tabview.html") {

/* --- Action bar --- */
#actions {
margin: 8px !important;
padding: 5px 0 5px 3px !important;
border-radius: 0 !important;
border: gray 1px solid !important;
box-shadow: none !important}

#exit-button {
background: none}

/* --- Search stuff --- */
#searchshade {
background: rgba(0,0,0,.75) !important;}

#search {
margin: -5px 0 0 5px !important}

#searchbox {
font-size: 14px!important;
border-radius: 0 !important;
box-shadow: 0px 3px 5px rgba(0,0,0,.3)!important;
color: black !important;
background-image: -moz-linear-gradient(top, #E0E9F2, #DAE5F0) !important}

/* --- Group box --- */
.groupItem {
border-radius: 0 !important;
border: gray 1px solid !important;
box-shadow: none !important}

/* --- Group title --- */
input.name {
margin-left: 10px !important;
font-weight: bold !important;
color: gray !important}

/* --- Tab --- */
.tab.focus{
box-shadow: 0 0 1px rgba(0,0,0,.5),
            0 0 5px rgba(0,0,0,.3) !important;
border-radius: 0 !important}

/* --- Item's title --- */
.tab-title {
padding-bottom: 1px !important;
font-size: 8pt !important;
text-shadow: 0 1px 0px rgba(255,255,255,1) !important;}

/* --- Close button --- */
.groupItem .close {
opacity: 0 !important;
-moz-transition: all 250ms ease-in 0ms !important}

.tab .close {
opacity: 0 !important;
-moz-transition: all 250ms ease-in 0ms !important}

.groupItem:hover .close {
opacity: 1 !important}

.tab:hover .close {
opacity: 1 !important}

/* --- Favicon --- */
.favicon {
opacity: 0 !important;
padding: 2px !important;
border-radius: 0 0 3px 0 !important;
-moz-transition: all 250ms ease-in 0ms !important}

.tab:hover .favicon {
opacity: 1 !important;
-moz-transition: all 250ms ease-out 0ms !important}


/* --- Resizer --- */
.groupItem .resizer {
margin: 0 4px 6px 0 !important;
opacity: 0 !important;
-moz-transition: all 250ms ease-in 0ms !important}

.groupItem:hover .resizer {
opacity: 1 !important;
-moz-transition: all 250ms ease-out 0ms !important}

/* --- Undo --- */
.undo {
border-radius: 0 !important;
color: #616364 !important;
box-shadow: 0 0 1px rgba(0,0,0,.1),
            0 2px 4px rgba(0,0,0,.3) !important;
background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important;
-moz-transition: all 250ms ease-out !important}

.undo:hover {
color: white !important;
background: rgba(0,0,0,.75) !important}

}
