css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document 

domain("netgear.com"),
domain("netgear.com.au"),
domain("forum1.netgear.com.au")
{
html, body, .hero, .sf-js-enabled, .sf-js-enabled a, .tcat,
#wrapper, #footer, .main8col, ul.css-tabs a.current span, .current,
 .toolsBox, .footer, .prodTitle, .prodBrowse, .prodTitleFooter,
 .sIFR-h1, #detail p, .page, .alt1, .alt2, .vbmenu_control, .thead,
.main div, .tcat_netgear_index, .alt1Active, .tfoot, .no-icon,
.drop-shadow, .odd, .icon, .main6col, .left6col, .Note td, 
.Note_2 td, .dropdown, .slides li, header .secondary-nav nav.first .active a, header .secondary-nav nav.first li a, .siblings, .shadow, .tabs,
.disclaimer, .rightnavtools, .productdownloadsarea, .panelsurround, #register, .ui-autocomplete,
.panel, input, select, textarea
{
background:#000 !important;
}
#ancDownload
{
background:#111 !important;
border:3px #444 solid !important;
}
.panel
{
border:none !important;
}
a, p, b, h2, h3, h4, h5, h6, ul, .smallfont, .alt1, .alt2,
.CellBullet_inner, .CellBody, .Note, .Bold, .s1_step_inner,
.Note_inner, .p2_body, .s2_step_inner, .pCellBody, .pCellHeading, select, label, textarea,
.content span, .panel, .panel a, input, legend
{
color:#FFF !important;
text-shadow: none !important;
}
a:hover {
text-decoration: underline !important;
}
}
