css:
@-moz-document url("about:newtab"){


#newtab-window div#newtab-scrollbox {
background: url("chrome://browser/skin/newtab/noise.png") repeat-x scroll 10% 0 #AAAFB7!important;
}

.newtab-site,
.newtab-site:hover, .newtab-site[dragged] ,
.newtab-cell {
border-radius: 0!important;
}

.newtab-site:hover, .newtab-site[dragged]:hover {
box-shadow: none!important;
}

.newtab-cell {
background-color: transparent!important;
border: 0!important;
}

.newtab-title {
background-color: transparent!important;
color: #444!important;
text-shadow: 0 1px 0px #eee!important;
font-size: 1.1em!important;
text-align: center!important;
}

.newtab-thumbnail {

margin-bottom: 2.5em!important;
background-color: transparent!important;
box-shadow: 0px 1px 9px rgba(0,0,0, .3)!important;
}


#newtab-margin-top {
max-height: 60px!important;
min-height: 40px!important;
}

#newtab-margin-bottom {
max-height: 50px!important;
min-height: 40px!important;
}

.newtab-control-block {
background-position: 0 0!important;
background: url("data:image/png;base64,")!important;
height: 16px!important;
width: 16px!important;

}

.newtab-control-block:hover {
opacity: 0.7!important;
}

.newtab-control-pin {
background: url("data:image/png;base64,")!important;
height: 16px!important;
width: 16px!important;
left:auto!important;
right:24px!important;
}

.newtab-control-pin:hover {
opacity: 0.8!important;
}

.newtab-control-pin[pinned] {
background: url("data:image/png;base64,")!important;
}
}
