css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(majorgeeks.com) {

/* cleanup */
body>div>table>tbody>tr>td>table>tbody>tr>td>center>font, /* top advert */
body>div>table>tbody>tr>td>table>tbody>tr>td>center>center>center>center>a>img, /* ... */
body>div>table>tbody>tr>td>img,
body>div>table>tbody>tr>td>p>table>tbody>tr>td>center>font /* bottom advert */
body>div>table>tbody>tr>td>p>table>tbody>tr>td>center>a>img, /* ... */
body>div>table>tbody>tr>td>center>table>tbody>tr>td>center>a>img,
/* body>div>table>tbody>tr>td>table>tbody>tr>td>img, */  /*hide images */
body>div>table>tbody>tr>td>center>font, /* left advert */
/* body>div>table>tbody>tr>td>center */
body>div>table>tbody>tr>td>hr, /* recommended advert */
body>div>table>tbody>tr>td>div>table>tbody>tr>td>center, /* ... */
body>div>table>tbody>tr>td>div>hr,  /* ... */
body>div>div>ul>li>div>table>tbody>tr>td, /* various adverts */
body>table>tbody>tr>td>div,
body>div>table>tbody>tr>td>center>center>center>center>a>img,
body>di>table>tbody>tr>td>p>table>tbody>tr>td>center>center>a>img,
body>div>div>ul>li>div>table>tbody>tr>td,
body>table>tbody>t>td
{ display: none !important; }

/*  Center page content */
body>div>table>tbody>tr>td>table>tbody>tr>td{
background: #fff !important;
border: 0px solid #d1d1a7 !important;
}


body {
background: white !important;
color: black !important;
font: 12px arial, helvetica, sans-serif;
}

a:link{
color: blue !important;
text-decoration: none !important;
}
a:visited{
color: purple !important;
}
a:hover{
color: blue !important;
text-decoration: underline !important;
}

table, tr, td{
background: white !important;
color: black !important;
border: none !important;
}

embed, object {
display: none !important;
}

}
