css:
@-moz-document domain("torrentday.com"), domain("torrentday.eu"), domain("tdonline.org") {
.fb,
.fbshare,
.love,
.tname > br,
body > div:nth-of-type(3),
body > table:last-of-type,
#ajax-feedback + div[align=center]
{
  display: none;
}
  

body
{
  font-family: "tahoma", "arial", "helvetica", "sans-serif";
  font-size: 10pt;
  background-color: white;
  color: hsl(0, 100%, 100%);
  margin-top: 0px;
  margin-bottom: 0px;
}
  

#ttable, #mainBox, #torrenttable, #T_table, #torrenttable > table
{
    width: 1200px !important;
    overflow: visible !important;
}


div.paging + table td[align=left],
#ttable td.tname
{
	font-size: 6pt;
	color: HSLA(0, 0%, 0%, 0.3) !important;
	overflow: hidden !important;
  	text-align: right !important;
}

span.tags.newtags {
	color: HSLA(0, 100%, 50%, 0.3) !important;
	margin: 0 1em;
}

div.paging + table td[align=left] > a,
#ttable td.tname[onclick],
#ttable td.tname > a.T_detail
{
	float: left;
	font-size: 8pt;
	color: black !important;
	text-decoration: none !important;
}


div.paging + table td[title=Name],
div.paging + table td a b,
#ttable td.tname > a.T_detail b,
#ttable tr.thead > td.tname {
  	text-align: left !important;
  	float: none;
 	font-size: 11pt;
}


div.paging + table td br,
#ttable td > br
{
	content: "&nbsp;";
	width: 0.5em;
  	display: inline-block;
}

div.paging + table td:nth-of-type(4),
#ttable td:nth-of-type(4)
{
	display: none;
}

a:visited b,
a:visited font
{
  color: hsl(0, 0%, 50%) !important;
}


a:visited b:before
{
  color: green;
  content:  "\2713 ";
  opacity: 0.5;
}
}
