css:
@-moz-document url("https://www.tumblr.com/login"), url("https://www.tumblr.com/"), url("https://www.tumblr.com/register"), url("http://www.tumblr.com/new/blog"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/help"), url("https://www.tumblr.com/preferences"), url("https://www.tumblr.com/lookup"), url("http://www.tumblr.com/explore"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/following"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/reblog/"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tumblelog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/edit/"), url-prefix("http://www.tumblr.com/activity/") , url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/following"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/activity/") {

Body {
  	background: url('http://i40.tinypic.com/15y6f5s.png') top left no-repeat fixed !important;
	color:#2a2a2a!important;
    font-family: trebuchet ms;
	font-size:12px !important;
   	background-size: 100%   !important;
  }


body, a, a:hover {
	cursor: url('http://i39.tinypic.com/2mpya86.png'), progress !important;
 }



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: none !important;
  	-webkit-border-radius: 15px;
	-moz-border-radius: 15px;
	border-radius: 15px;
  }


a:link, a:active, a:visited {
  	color: #00C480 !important; 
  	text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 


a:hover {
  	color: #FCFFA8 !important;
  	text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 
    

.post_info a:link, .post_info a:active, .post_info a:visited {
  	color: #8C00FF !important;
	-webkit-transition: all 0.3s linear;
  	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    text-decoration: none !important;
    font-family: trebuchet ms;
  } 
    
.post_info a:hover {
  	color: #008CFF !important; 
    text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
    -moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 


.notification  {
  	background: #930019 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }


.notification :link, .notification :active, .notification :visited {
  	color: #c1c1c1 !important;
    text-decoration: none !important;
    border: none !important;
  }


h1 {
  	color:none !important;
  }


div.l-header .tab.iconic a {
  	background-color: #7400D4 !important;
  	border-radius: 50px !important;  
    -webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;

  }


div.l-header #home_button a {
  	background-color: #7400D4;
  	background-position: 0 0!important;
  	border-radius: 50px !important;  
  	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;

  }


div.l-header .selected .selection_nipple {
	display: none;
  }


.avatar_frame {
  	border: none !important;
    background-color:"{PortraitURL-24}" !important;
    border-radius: 50px;
  }


.post_avatar {
	border: 2px solid #000000 !important;
    box-shadow: 0px 0px 7px #fff !important;
  }


.post_avatar .post_avatar_image, .post_avatar .post_sub_avatar_image {
	display: block;
	opacity: 0;
}


.search_query {
	display: block;
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	height: 100%;
	border-width: 0;
	background: transparent;
	color: #7400D4;
	font-weight: bold;
	-moz-user-select: text;
	-webkit-font-smoothing: antialiased;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
  }


.post_full {
       
       background: none !important;
       box-shadow: none !important;

  }


.post_full .post_body p {
    padding: 10px;
    background-color: transparent !important;
    font: 11px courier,monospace;
    overflow: auto;
}

.post_full .post_permalink {
	width: 23px;
	height: 23px;
	background: url('http://static.tumblr.com/vv7anc1/Xs6mo4woz/perma.png') !important;
  }

.post .audio_player {
background-color: #666666;
color: rgba(255, 255, 255, 0.73);
}

#right_column {
  	background: transparent !important;
}

#tinymce .mceContentBody {
background-colour: #fff !important;

}

}
