css:
@-moz-document domain("officetanaka.net") {

  #top-image, .menu, a[href*="seminar"], iframe, .titlebar,
  center>input, #foot, #navi+br+#contents
  { display: none !important; }

  body {
    background-color: #f2f2f2;
    color: #444;
  }
  #frame {
    background-image: none;
    border: 0;
    margin: 4em auto;
    border-radius: 4px;
    box-shadow: 0 1px 3px 0 #ddd;
  }
  .box {
    border: 1px solid #eee;
  }
  h2 {
    border-width: 0 0 0 .5em;
    padding-left: 8px;
    margin-top: 2em;
  }

}
