css:
@namespace url(http://www.w3.org/1999/xhtml);
  
@-moz-document domain("game-of-thrones.1fr1.net") {
* { 
background-color:#ddd !important; 
box-shadow:none !important;
}
  
body {
    background-image:none !important; 
    background-color:#ddd !important;
}
body[style] {
    background-color:#ddd !important;
}

input, textarea, select, h1, h2 {
color:inherit !important;
}
.bodylinewidth{
width:100% !important; 
}

.name {
color : inherit !important;
font-style : italic;
text-decoration: underline;
}
span.nav {
color : #555 !important; 
 }
div.cssactif_fiche
{
width:100% !important; 
}
div.signature_div{
display:none;
}
#englob { display : none}
  
div.profilentour {
background-image:none !important;
}
  
div.profilchamps { display : none }
div.profilentour img {
display:none;
}
.postbody {
color:black !important;
font-size: 1.2em !important;
line-height: 1.5em !important;
}
a{
color : blue !important;
border : none !important;
}
.usernamefont {
border-bottom: 1px solid #777 !important;
}
tr.post * {
background-color:#eee !important;
}
  
table.forumline > tbody > tr{
background-color:#eee !important;
}
table.forumline > tbody > tr:nth-child(4n+5) {
background-color:#ddd !important;
}
  
.postdetails {
color:#222 !important;
}
td.catHead { display:none; }
  
tr.post + tr { display : none; }
  
table.forumline tr * {
background-color:inherit !important;
}
  
table.forumline {
border : none ! important;
}
table.forumline:nth-child(7) > tbody:nth-child(1) > tr:nth-child(2) {
display:none;
}
#i_logo {
display:none;
}
.bodyline > table:nth-child(1) { display : none; }
#page-footer { display: none;}

  
/* Page ppale */
.gensmall {
color:inherit !important;
}
div.gensmall { display : none;}
#emptyidcc table.three-col table.forumline > tbody > tr > td.row1 > img {
/*height: 10px; */
/*padding: 5px;*/
display :block;
}
.block {
background-image:none !important;
opacity:1 !important;
border:none !important;
}
/*
div.cssactiv_pv_content[style] {
    height:100% !important;
}
*/
.tite {
background-image:none !important;
border:none !important;
}
.genmed{
color:inherit !important;
}
  
img.desc_forum {
opacity:1 !important;
border:none !important;
}
.gen{
color:inherit !important;
}
/* Chatbox */
#chatbox{
background-color:#333 !important;
}
#chatbox * {
background-color:inherit !important;
}

.titreqeel {
display:none;
}
.groupes {
padding:0 0.2em !important;
}



/* Overwrite inlined styles from posters when possible */
div.postbody div[style] {
font-size:inherit !important;
background-image:none !important;
width:100% !important;
}

}
