css:
@-moz-document url-prefix("https://imo.im") {
div#buddypanel div#buddysearch {
  height: 47px;
}

div#buddypanel div#buddysearch>* {
  margin-top: 15px;
}

div#buddypanel div#buddysearch #buddysearch_input_div {
  margin: 9px 0;
}

div#buddypanel div.blist-container {
  top: 48px;
}

.buddygroup:nth-child(1) {
  display: none;
}

.buddyitem .imgwrap {
  border-radius: 100%;
  overflow: hidden;
  box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.4);
}

div#buddypanel div.blist-container.icon_view .buddyitem {
  height: 25px;
  background: none;
  border: none;
  margin-bottom: 1px;
  font-size: 12px;
}

div#buddypanel div.bar:hover, 
div#buddypanel div.bar.selected,
div#buddypanel div.blist-container div#blist div.buddygroup div.buddygrouplist div.buddyitem:hover {
  background: #f2f2f2;
}

div#buddypanel div.blist-container.icon_view .buddyitem span.imgwrap,
div#buddypanel div.blist-container .buddyitem .buddyimg {
  display: none;
}

div#buddypanel div.blist-container.icon_view .buddyitem span.accountdisplay {
  height: 100%;
  display: block;
  
  border-left: 4px solid #a0a0a0;
  padding-left: 10px;
}

.convlog .me + .me .na,
.convlog .me + .me .ts-container,
.convlog .bk0 + .bk0 .na,
.convlog .bk0 + .bk0 .ts-container,
.convpanel .convbuttons {
  display: none;
}
    
.tabbar {
  width: 350px;
  height: auto;
        
  bottom: 0;
  
  background: #eaeff4;
  border: none;
}
      
.tabbar .visible-tab-list.no-arrows,
.tabbar .visible-tab-list.arrows {
  background: none;
  border: none;
  
  right: 0;
  left: 0;
}

.tabbar .visible-tab-list .complete-tab-list {
  width: 100% !important;
}
      
div.titlebar-tabbed {
  width: 100%;
  height: auto;
  float: none;
  background: none;

  border: 1px solid #d1d7dc;
  border-top: none;
  border-left: none;
}

div.titlebar-tabbed div.titleicon {
  height: auto;
}

div.titlebar-tabbed.focused {
  background: white;
  border: none;
  border-bottom: 1px solid #d1d7dc;
}

div.titlebar-tabbed div.titleicon {
  width: auto;
  line-height: 50px;
}
            
div#body {
  bottom: 0;
}
      
div.titlebar-tabbed div.titleicon img,
div.titlebar-tabbed img.close,
.tabbar .right-more-indicator,
.tabbar .left-more-indicator,
.tabbar .menu-button,
.convpanel div.convmain div.convbottom div.primary-button,
div#footer,
div#buddypanel div.bar.white .button {
  display: none;
}
        
div.titlebar-tabbed:hover img.close {
  display: block;
  opacity: 0.5;
  margin: 5px;
}
              
div.titlebar-tabbed:hover img.close:hover {
    opacity: 0.8;
}
          
div.titleicon span {
  display: block;
  border-left: 4px solid #a0a0a0;
  margin: 0;
  padding-left: 10px;
  font-weight: bold;
  font-size: 13px;
}
        
div.titlebar-tabbed div.titleicon img[class*="away"] + span,
div#buddypanel div.blist-container.icon_view .buddyitem img[class*="away"] + span.accountdisplay {
  border-left-color: #ffbc2f;
}
        
div.titlebar-tabbed div.titleicon img[class*="available"] + span,
div#buddypanel div.blist-container.icon_view .buddyitem img[class*="available"] + span.accountdisplay{
  border-left-color: #47be24;
}
        
div.titlebar-tabbed div.titleicon img[class*="busy"] + span,
div#buddypanel div.blist-container.icon_view .buddyitem img[class*="busy"] + span.accountdisplay{
  border-left-color: #c41826;
}

div#buddypanel div.blist-container.icon_view .buddyitem .control_container {
  margin-top: -8px;
}
            
.content {
  top: 0;
  left: 350px;
  
  border: none;
}
          
div#buddypanel .collapsed,
.convpanel div.convmain div.convbottom {
  background: none; 
}
            
.convpanel div.convmain div.convbottom div.convinput {
  background: #eaeff4;
  border-radius: 2px;
  border: 1px solid #C1C1C1;
  margin: 5px 10px;
          
  right: 0;
}

div#body div#buddypanel,
div.bar.white {
  border: none;
}

div#buddypanel div.bar.white span.title {
  left: 0;
}

.buddygroup .bar.white {
  height: 25px;
  line-height: 25px;
  font-size: 12px;
  margin-top: 10px;
  margin-left: 0;
  padding-left: 0;
}

.convlog .na {
  font-size:13px;
  display: block;
  margin: 25px 5px 5px;
}

.convlog .sys_online .ts,
.convlog .sys_offline .ts,
.convlog .ts-container {
  float: right;
  margin: 5px;
}
        
.convlog .convlogitem.bk0 {
  background: none;
}

.convlog-msgs {
    padding: 0 5px;
}
  
.convpanel div.convmain {
  top: 0;
}
}
