css:
@-moz-document domain("www.pixiv.net") {
ul.novel-items li section._novel-item ul.tags a,
div.novel-item ul.tags a
{
color:#dddddd!important;
}
ul.novel-items li section._novel-item ul.tags a:hover,
div.novel-item ul.tags a:hover
{
color:#666666!important;
}
ul.novel-items li section._novel-item
{
margin-left:0px!important;
margin-right:0px!important;
min-width:450px!important;
width:450px!important;
}
div#column-misc section.item,
footer.footer
{
margin-top:0px!important;
}
header.header span.count-badge,
a[href^="https://serv.ads.pixiv.org/"],
a[href^="https://matsuri.pixiv.net/"],
div#header-banner,
div.ui-layout-west section.my-profile-unit ul.badges li:nth-of-type(2),
div.ui-layout-west aside#user-recommend-container,
div.ui-layout-west aside.side-menu,
div#column-misc section.ad,
div#column-misc section.recruit-ad,
div#item-container dl.velocity,
div#item-container section.sketch-live section.content,
div#item-container section.recommended-illusts section.content,
div#item-container section.showcase section.content,
div#item-container section.fanbox section.content,
div#item-container section.contests section.content,
div#item-container section.sensei-courses section.content,
div#item-container section.spotlight section.content,
div#item-container section.booth-follow-items div.information,
div#item-container section.booth-follow-items div.description,
div.contents-main section.standard-tag,
div.contents-main div.project_pixiv,
div.contents-main div#pickup-contest-container-mini,
div.contents-main ul.tagCloud li,
div.contents-main ul.more,
div.contents-main div.more,
div.contents-main div[style="margin:30px 0 0 0;"],
div.contents-main dl.velocity
div#wrapper div#header-banner,
div#wrapper div.ad-footer,
div#wrapper div.rounded ul.top-info-content
{
display:none!important;
}
}
@-moz-document url("https://www.pixiv.net/"), url("https://www.pixiv.net/cate_r18.php"), url("https://www.pixiv.net/novel/"), url("https://www.pixiv.net/novel/cate_r18.php"), url-prefix("https://www.pixiv.net/mypage.php") {
div#item-container section.item section.content section
{
margin-top:0px!important;
border-top:0px!important;
}
div#wrapper a[href^="/premium.php?ref=mypage_top"],
div#wrapper a[href^="/premium.php?ref=premium_campaign"],
div.ui-layout-west div.area_new
{
display:none!important;
}
}
@-moz-document url-prefix("https://www.pixiv.net/member_illust.php"), url-prefix("https://www.pixiv.net/novel/show.php"), url-prefix("https://www.pixiv.net/artworks/") {
span.tags-container ul.tags li.tag a
{
color:#dddddd!important;
}
span.tags-container ul.tags li.tag a:hover
{
color:#666666!important;
}
div._comment-items ._comment-item,
div._comment-items ._comment-item .comment,
div._comment-items ._comment-item .comment .meta
{
min-height:0px!important;
margin:0px!important;
padding:2px 0px!important;
}
div#wrapper div.manage-unit aside,
div#root section.sc-LzNbx,
div#root div.sc-LzOjK,
div.works_info div.worksOptionRight,
div._comment-items ._comment-item img,
div._comment-items ._comment-item ul.action-list,
div._comment-items ._comment-sticker-item,
footer.footer
{
display:none!important;
}
}
@-moz-document url-prefix("https://www.pixiv.net/novel/member.php"), url-prefix("https://www.pixiv.net/novel/bookmark.php"), url-prefix("https://www.pixiv.net/novel/marker_all.php") {
ul.novel-items li section._novel-item
{
margin-left:0px!important;
margin-right:0px!important;
min-width:360px!important;
width:360px!important;
}
ul.novel-items li section._novel-item table.stats *,
ul.novel-items li section._novel-item p.update-date
{
white-space:nowrap!important;
}
ul.novel-items li section._novel-item ul.edit
{
float:none!important;
}
}
