css:
@-moz-document domain("deviantart.com"), domain("sta.sh") {
.username {color: #304030 !important;}
#overhead .username {color: #c0d0ca !important;}
#depths .username {color: #d8e4d8 !important;}
.collections .username {color: #CCC !important;}
.journal .username {color: inherit !important;}
.journal .user-symbol {color: inherit !important;}

.user-symbol {font-size: 57.5% !important;
	color: #778584 !important;
	padding: 0 !important;
	vertical-align: text-top !important;}
#overhead .user-symbol {color: #93aa9b !important;}

.external:after {display: none !important;}

.symbols-modal .user-symbol {font-size: 42px !important;}

.quicktip {margin-left: -5px !important;}
}
