css:
@-moz-document url('https://www.tumblr.com/'),

  url('http://www.tumblr.com/'),

  url-prefix('https://www.tumblr.com/'), 

  url-prefix('http://www.tumblr.com/') 
{



body {
  
background:#dddddd url(http://25.media.tumblr.com/9b2ca4f799cd275e9c8dcb4066cfe74e/tumblr_mu336mQyGQ1sk768qo1_1280.png) no-repeat bottom right fixed! important;background-size: 100% fixed! important;



}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(100,100,100,0.1) !important;;
}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {


  background: transparent !important;


  width: 0px !important;


  height: 0px !important;


  margin-top: 0px !important;


  border-right: 11px transparent !important;


  border-left: 11px transparent !important;


  border-bottom: 1px transparent !important;

  opacity: 1 !important;
}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important; /*remove ugly blue radar border frame from existence*/
}


.post.photo_post .photo, .post.photo_post .video_thumbnail  {
  border-radius: 16px !important;/*change to 10px to match the curve of the right column boxes - 16px for iconbar box*/
}


.mceContentBody {


  color: rgba(0,0,0,0.8) !important;


  background: rgba(0,0,0,0.8) !important;
}


.radar { display: none !important }
}
