css:
@-moz-document domain("web2.qq.com/webqq.html"){

.EQQ_BuddyList_Nick, .EQQ_BuddyList_Sign, .EQQ_GroupList_Bulletin {padding-top:10px !important}
 
img.EQQ_BuddyList_Avatar{padding:0px !important}
 
#EQQ_LoginSuccess{font-size: 16px!important;
}
.eqq_window .window_bg_container
{border: none !important; background-image:none !important;}
 
.eqq_window .window_bg_container, .EQQ_tab, .EQQ_ListBottom
{background-color:#d4d4d4 !important;}
 
.EQQ_tab .current {
background: #F5F5F5 !important;
}
 
.EQQ_buddyListPanel, .EQQ_groupTab, .EQQ_groupListOuter, .EQQ_recentListPanel {background-color: #F5F5F5 !important;
}
 
.EQQ_tab{height:28px !important;}
 
.EQQ_List_BigHead .EQQ_BuddyList_Buddy, .EQQ_List_BigHead .EQQ_GroupList_Group
{padding:0px!important;height:32px!important;}
 
.EQQ_BuddyList_Sign, .chatBox_announcementArea, .EQQ_BuddyList_Sign, .EQQ_GroupList_Bulletin, .chatLogTips
{display:none!important;}
 
.EQQ_listClassBody, .EQQ_BuddyList_Buddy, .EQQ_GroupList_Group{
border-bottom: 1px solid #cecece!important;
-webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, .08)!important;
}
  
.EQQ_List_BigHead .EQQ_BuddyList_AvatarContainer, .EQQ_List_BigHead .EQQ_GroupList_AvatarContainer {
height: 32px !important;
width: 32px !important;
}
 
.EQQ_tabBuddyList_icon, .EQQ_tabGroupList_icon, .EQQ_tabRecentList_icon {background:none !important}
 
/* 主窗口 End */
 
/* 聊天窗口 */
 
.chatBox_mainArea {background-color:#F5F5F5 !important;}
 
.chatBox_toolBar, .chatBox_controlPanel, .chatBox_toolBar_top,  .chatBox_sideBar2
{background-color:#d4d4d4 !important;}

.chatLogMain{top:26px !important}
 
/* 聊天窗口 End */}
