/* Google Grid enhancements Plus
   This style was made to demonstrate a possible
   configuration of the Google Grid. Feel free
   to modify and repost with your own stuff or
   to fit certain Google themes! 

   Now updated for Google+ enhancements!
   Special thanks to manieKMP for helping out.
   http://userstyles.org/users/178876 
*/

@namespace url(http://www.w3.org/1999/xhtml);
/* Old regexp; Kept for legacy purposes, you can safely remove it. */
/*@-moz-document regexp("https?://(www|images)(?!(docs|play))\.google\.com.*")*/

/* New regexp; More universal, should work everywhere. */
@-moz-document regexp("https?:\/\/.*\.google\.*") {

/* Reposition, add global padding, remove shadow */
.gb_I,
.gb_bb,
.gb_Sb,
.gb_la.gb_H{
  margin-top: 10px !Important;
  box-shadow: none !Important;
  padding: 8px !Important;
}

/* Bubble arrows */
.gb_ja,
.gb_ka{
  display: none !important;
}
/* Text prefs */
.gb_u,
.gb_5b,
.gb_Zb,
#fsett * {
  color: white !important;
  text-shadow: 0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d !important;
}
.gb_t,
.lOa:hover { /* Because I can */
  text-decoration: underline !important;
}
.gb_4b:hover,
.gb_Xb:hover {
  text-decoration: none !important;
}
.gb_p:nth-child(2),
.gb_p:nth-child(3),
.gb_p:nth-child(5),
.gb_p:nth-child(6),
.gb_p:nth-child(8),
.gb_p:nth-child(9){
  margin-left: 3px !Important;
}
.gb_p:nth-child(4),
.gb_p:nth-child(5),
.gb_p:nth-child(6),
.gb_p:nth-child(7),
.gb_p:nth-child(8),
.gb_p:nth-child(9){
  margin-top: 3px !Important;
}
/* Individual items */
.gb_p { /* Item background */
  background-color: rgba(0,0,0,0.25) !important;
  border-radius: 4px !important;
  border: none !important;
  padding: 0px !important;
}
.gb_p:hover {
  background-color: rgba(0,0,0,0.50) !important;
}
.gb_u { /* Text background */
  background-color: transparent !important;
}
.gb_p:nth-child(4) .gb_t,
.gb_p:nth-child(5) .gb_t,
.gb_p:nth-child(6) .gb_t{
  padding-top: 1px !Important;
}
.gb_p:nth-child(7) .gb_t,
.gb_p:nth-child(8) .gb_t,
.gb_p:nth-child(9) .gb_t{
  padding-top: 2px !Important;
}
/* Google Grid and widgits */
.gb_bb,
#gbsfw{ /* G+ Notifications widgit */
  background-color: rgba(0,0,0,0.25) !important;
  border-color: rgb(153, 153, 153) !important;
  border-radius: 8px !important;
}
/* Item containers */
.gb_A{ /* Top */
  background-color: rgba(0,0,0,0.25) !important;
  border-radius: 6px 6px 0px 0px !important;
}
.gb_B { /* Bottom */
  background-color: rgba(0,0,0,0.25) !important;
}
/* "More" buttons */
.gb_5b { /* Top */
  background-color: rgba(0,0,0,0.25) !important;
  border-radius: 0px 0px 6px 6px !important;
}
.gb_5b:hover {
  background-color: rgba(0,0,0,0.50) !important;
}
.gb_Zb { /* Bottom */
  background-color: rgba(0,0,0,0.25) !Important;
  width: 320px !important; /* Position settings */
  margin-left: -28px !important;
  border-radius: 0px 0px 6px 6px !important; /* Looks, mostly */
}
.gb_Zb:hover {
  background-color: rgba(0,0,0,0.50) !important;
}
/* Make the white line between item containers disappear */
#gbwa > DIV:last-child > SPAN {
  display: none !important;
}
/* "Settings" menu stuff */
#fsett {
  margin-bottom: 18px !important;
  background-color: rgba(0,0,0,0.25) !important;
  border-radius: 8px !important;
}
#fsett * { /* Link items */
  background-color: rgba(0,0,0,0.25) !important;
  margin-top: 4px !important;
  margin-bottom: 4px !important;
  border-radius: 6px !important;
  text-align: center !important;
}
#fsett *:hover {
  background-color: rgba(0,0,0,0.50) !important;
}
/* Positional fine-tuning */
#fsett > A:first-child {
  margin-top: 0px !important;
}
#fsett > A:last-child {
  margin-bottom: 0px !important;
}
/* Stuff specific to Google Plus */
.aac {                                    /* Enable see-through elements for */
  background-color: transparent !important; /* other widgets, thanks manieKMP! */
}
/* G+ notification widget */
.UtvBtc {
  background-color: rgba(0,0,0,0.25) !important;
  margin-left: 0px !important;
  width: 400px !important;
  border-radius: 6px !important;
}
.UtvBtc:hover{
  background-color: rgba(0,0,0,0.50) !important;
}
.o6V5ne{
  background-color: white !Important;
  border: 1px solid white !Important;
  width: 11px !Important;
  height: 11px !Important;
  border-radius: 1000% !important;
  /*background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/events-fad4e36111e05d03c08404e3e9e2e45d.png) !important;*/
}
.Cwb, /* Notification widget text colors */
.YGETNc,
.DEVjuc {
  color: white !important;
}
.fc /* G+ Share widget */ {
  padding: 0px !important;
}
.fc,
.xja,
.zR { /* Grey stuff */ 
  background-color: transparent !important;
}
/* Share add-in menu */
.xja {
  margin-left: 0px !important;
}
.g-h-f-Au { /* "Browse people" icon */
  background-color: white !important;
  margin-right: -40px !important;
}
.Rd { /* Top textbox */
  width: 458px !important; /* Top box stuff */
  border-radius: 6px 6px 0px 0px !important;
}
.URaP8.editable:not(.b-K-Xb) { /* Bottom box stuff */
  background: transparent !important;
  width: 458px !important;
}
.jt { /* G+ Share "Share / Invite" button */
  background-color: rgb(62,127,47);
  border-color: rgba(0,0,0,0.25) !important;
  border-radius: 6px !important;
}
.jt:hover {
  background-color: rgb(83,169,63);
}
.xD { /* "Cancel" button */
  background-color: rgba(0,0,0,0.25) !important;
  border-color: rgba(0,0,0,0.25) !important;
  border-radius: 6px !important;
  margin: 0 !important;
  position: absolute !important;
  right: 0 !important;
}
.xD:hover {
  background-color: rgb(215,61,50) !important;
}
.xD:focus {
  outline: none !important;
}
.xja *,
.xD, #sbxeoh * { /* Share widget text colors */
  color: white !important;
}
span.d-s.ph.qZ { /* Spacing for share options */
  padding-left: 5px !important;
}
#sbxeoh { /* Privacy warning stuff */
  width: 300px !important;
}
#sbxeoh > div.pF.Pra { 
  float: left !important;
}
/* Decomment below for testing privacy dialog functions */
/*#sbxeoh > div {
  display: block !important;
}*/
.YHa{ /* Share: Photos */
margin: auto !Important;
}
.yra,
.Hla {
width: 480px !important;
}
.MHa,
.fya { /* Photo buttons */
  width: 203px !Important;
  background-color: rgba(0,0,0,0.25) !important;
  border-color: transparent !important;
}
.MHa:hover,
.fya:hover
{
  background-color: rgba(0,0,0,0.50) !important;
}
.FC { /* Share: Link */
  padding-right: 36px !Important;
  width: 356px !Important;
}
.fm {
  color: black !important;
  width: 316px !important;
}
.XK { /* Link error */
  background: transparent !important;
  margin-bottom: -9px !Important;
}
.dL {
  text-shadow: 0px 0px 6px red,
               0px 0px 6px red,
               0px 0px 6px red !important;
}
.HC { /* "Add" button */
background-color: rgba(0,0,0,0.25) !important;
border-color: transparent !important;
border-radius: 6px !Important;
}
.HC:hover {
background-color: rgba(0,0,0,0.50) !important;
}
.Hc.xja {/* Share: Video */
position: relative !important;
}
.I8 {
  margin: auto !important;
  max-width: 480px !important;
  width: 346px !important;
}
.SJ{ /* Removes black gradient in screenshot */
  background-image: none !Important;
}
.zn .Nf, .Rj .Nf {
  background-color: transparent !important;
  border: none !important;
  padding: 0 !important;
}
.Pha { /* Button stuff */
  opacity: 1 !important;
  max-width: 20px !important;
  position: absolute !important;
            right: 72px !important;
            top: 8px !important;
  z-index: 1 !Important;
  background: url(http://ssl.gstatic.com/s2/oz/images/sprites/events-fad4e36111e05d03c08404e3e9e2e45d.png);
  background-position: -45px -51px !Important;
}
.Oha{ /* Hide original close button for .Pha to supersede */
  background: none !Important;   
}
.Pha > div:last-of-type {
display: none !important;
}
.kq.ot-anchor {
  padding: 6px !Important; /* Fixes text-shadow cutoff */
  margin-top: -6px !Important;
  margin-left: -6px !Important;
  text-shadow: 0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d !important;
}
/* Share: Event (Completed by manieKMP) */
.d-y-r-c-ha,
.d-A *,
.b-Rv *,
input.o-E-N,
.pq {
  color: black !important;
}
.mOa{
  position: absolute !Important;
  top: 10px !Important;
  right: 5px !Important;
}
input.o-E-N.d-k-l,
input.o-E-N.Eaa,
div.o-E-N{
  width: 470px !important;
}
.Yfa { /* End time open */
  float: right !important;
  width: auto !important;
  height: auto !important;
  padding-top: 2px !important;
}
.Jpa { /* Timezone settings fix by manieKMP */
  width: 300px !Important; 
}
.Ipa .d-y-r-c-Qa { /* For 356's left value, 353's width - 3 */
  right: auto !important;
  left: 297px !important; /* A/N: I would going for something different, */
}                       /*      but this is reasonable too. Thanks! */
.bEa{ /* Spacing for end time */
 padding-right: 10px !Important;
}
.lOa { /* "End time" close: Remove icon and reposition */
  position: absolute !Important;
  top: 185px !important;
  right: 0px !important;
  background-image: none !Important;
  width: auto !important;
  height: auto !Important;
}
.lOa:after { /* Label, change to your language as necessary */
  content: "Remove end time" !important;
}
.Hc,
.vpa,
.Rt .ux,
.Rt .Xq,
.Rt .Ym,
.Bka { /* Overall width settings */
  width: 480px !important;
}
/* Share: Poll */
.qYaiXb { /* Top input */
  border-radius: 8px 8px 0px 0px !important;
  margin-top: -14px !Important;
}
.uX9dGb{
    margin-left: 12px !Important;
}
.GjrNhc,
.PAw83c { /* Text colour */
  color: black !Important;
}
DIV:nth-child(2) > DIV > DIV:nth-child(2) > DIV:not(.P3Wtpd):first-child > INPUT,
DIV:nth-child(2) > DIV > DIV:nth-child(2) > DIV:not(.P3Wtpd):last-child > INPUT{ /* Adjust C1 and C2 */
  width: 234px !Important;
}
DIV:nth-child(2) > DIV > DIV:nth-child(2) > DIV:not(.P3Wtpd):last-child > INPUT{ /* Repos C2 */
  margin-left: 1px !Important;
}
DIV:last-child > DIV:nth-child(2) > DIV > DIV:first-child > .ej{ /* Repos 2-img poll */
  margin-left: 8px !Important;
}
DIV:last-child > DIV:nth-child(2) > DIV > DIV:nth-child(2) > .ej{
  margin-left: 12px !Important;
}
.P3Wtpd,
.P3Wtpd > .GjrNhc,
.FOEPld { /* Image region & "More choices" fields */
  width: 480px !Important;
}
.PAw83c{ /* Multi-image poll fields */
  width: 418px !Important;
}
.uX9dGb,
.BzLxJe,
.oRKfqb{ /* Hide stuff */
  background: transparent !Important;
  background-image: none !Important;
}
/* Account panel */
.gb_H { /* Panel */
  border-color: rgb(153, 153, 153) !important;
  border-radius: 8px !important;
  background-color: rgba(0, 0, 0, 0.50) !important;
}
.gb_ua { /* Bottom */
  background-color: transparent !important;
}
.gbip { /* Avatar */
  border-radius: 4px !Important;
}
.gb_ra { /* Avatar action */
  border-radius: 0px 0px 4px 4px !Important;
}

:not(.gb_2b).gb_Z { /* Button colour */
  background-color: rgba(0, 0, 0, 0.25) !important;
}
:not(.gb_2b).gb_Z:hover {
  background-color: rgba(0, 0, 0, 0.50) !important;
}
.gb_Z { /* Button shape */
  border-radius: 4px !Important;
  border-color: transparent !Important;
}
.gb_sa,
.gb_ta,
.gb_ma A,
:not(.gb_2b):not(.gb_8b).gb_Z { /* Text shadow */
  color: white !Important;
  text-shadow: 0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d,
               0px 0px 6px #2d2d2d !important;
}
DIV:first-child > A > SPAN{
  text-shadow: none !Important;
}

}
