css:
@-moz-document url-prefix("http://feverclan.com/forums") {

#header 
{
  background-image:url(http://black-pixel.net/stuff/feverclan/6476319feverlogomaxreswithtextSMALL.png);
  background-repeat:no-repeat;
  height:220px;
}

#header img
{
  display:none;
}

/** Links **/
ul.isuser a
{
  -webkit-text-stroke:1px black;
  color:white;
  text-shadow:3px 3px 0 #000, -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
}

div.forumhead a
{
  text-shadow:-2px -2px 0 #000, 2px -2px 0 #000, -2px 2px 0 #000, 2px 2px 0 #000;
}

div.forumhead a:hover
{
  color:#000!important;
  text-shadow:-1px 0 white, 0 1px white, 1px 0 white, 0 -1px white;
  text-decoration:none;
}

/** Navigation **/
li#vbtab_cms :hover,li#vbtab_forum :hover,li#vbtab_whatsnew :hover,li#vbtab_blog :hover,li#tab_mdq4_622 :hover,li#tab_mdq4_866 :hover,li#tab_mdkz_683 :hover,li#tab_mji4_926 :hover
{
  border:2px solid black;
  -moz-box-shadow: 3px 0 3px -1px #222, inset -3px 0 3px -1px #222;
  -webkit-box-shadow:inset 3px 0 3px -1px #222, inset -3px 0 3px -1px #222;
  box-shadow:inset 3px 0 3px -1px #222, inset -3px 0 3px -1px #222;
}

li#vbtab_cms.selected,li#vbtab_forum.selected,li#vbtab_whatsnew.selected,li#vbtab_blog.selected,li#tab_mdq4_622.selected,li#tab_mdq4_866.selected,li#tab_mdkz_683.selected,li#tab_mji4_926.selected
{
  border:2px solid black;
}  

/** toplinks **/
#toplinks
{
  background-image:none;
  -moz-border-radius:5px;
  border-radius:5px;
  height:40px;
  line-height:35px;
}

/** Boxshadows for several elements **/
#navtabs,div.body_wrapper,div.footer1
{
  -moz-box-shadow:0 0 5px 5px #000;
  -webkit-box-shadow:0 0 5px 5px #000;
  box-shadow:0 0 5px 5px #000;
}

ul#sidebar
{
  -moz-box-shadow:0 0 5px 5px #111!important;
  -webkit-box-shadow:0 0 5px 5px #111!important;
  box-shadow:0 0 5px 5px #111!important;
}

div#footer,div#sidebar_container.profile_widgets,div.forumhead.foruminfo.L1.collapse,div#above_threadlist_controls,div#thread_controls,a#newthreadlink_top,a#newthreadlink_bottom,a#newreplylink_bottom,a#newreplylink_top,li.postbitlegacy,input#qr_submit,input#qr_preview,input#qr_cancelbutton,a.textcontrol,div#pagination_bottom a.popupctrl,div.group input.button,input#manage_attachments_button
{
  -moz-box-shadow:0 0 3px 3px #111;
  -webkit-box-shadow:0 0 3px 3px #111;
  box-shadow:0 0 3px 3px #111;
}

.forumrow.table,li .forumrow.table,li.threadbit,h2.blockhead,div.blockbody
{
  -moz-box-shadow:-5px 0 5px -5px #000, 5px 0 5px -5px #000;
  -webkit-box-shadow:-5px 0 5px -5px #000, 5px 0 5px -5px #000;
  box-shadow:-5px 0 5px -5px #000, 5px 0 5px -5px #000;
}

}
