css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://digg.com/reader") {
.modal {max-width:100% !important;max-height:100% !important;}
@media (max-width:733px) {
.modal {left:0 !important;top:0 !important;margin:0 !important;}
}
@media (max-width:688px) {
.content-primary {overflow:hidden !important;}
.content-main {width:100% !important;min-width:100% !important;}
.col-left {
margin-left:-250px !important;
transition:all 0.3s !important;
  z-index: 9999 !important;

}
.col-right {margin-left:0px !important;min-width:0 !important;transition:all 0.3s !important;}
.col-left:hover {
margin-left:0 !important;
}
.col-left:hover + .col-right {margin-left:251px !important;}
.col-left:after {
content:url(http://google.github.io/material-design-icons/navigation/svg/design/ic_menu_24px.svg);
position:absolute;
z-index:9999;
margin-top:13px;
left:100%;
top:0;
}
.story-detail-content {max-width:100% !important;}
.detail-body img  {max-width:90% !important;margin:0 auto !important;display:block !important;}
.feed-stream-header {position:sticky !important;top:0;z-index:999 !important;background-color:#FFF;}
.site-header-nav>ul {
display:none !important;
position:absolute !important;
top:100% !important;
}
.site-header-nav:after {
content:"...";
display:inline-block !important;
position:absolute;bottom:0;top:0;right:0;
padding:0 10px;
font-size:30pt;
line-height:75%;
font-weight:bold;
}
.site-header-nav:hover>ul {
display:block !important;
background-color:#FFF !important;
box-shadow: 0px 0px 2px 0px rgba(0, 0, 0, 0.5);
width: 159px;
right:0 !important;
}
.site-header-nav:hover:after {
background-color:#FDFDFD !important;
border-left:1px solid #D9D9D9;
}
.reader-nav .reader-nav-settings:before {
content:"Settings";
font-size: 12px;
font-weight: bold;
vertical-align:middle;
margin-top:15px;
margin-left:48px;
display:inline-block;
}
.reader-nav .reader-nav-text-item {
padding-left:20px !important;
width:auto !important;
margin:0 !important;
display:block !important;
float:none !important;
}
.reader-nav .reader-nav-text-item a {
font-size: 12px !important;
font-weight: bold;
color: #7A7A7A !important;
display:block !important;
}
#feed-title {margin-left:40px !important;}
.reader-nav .reader-nav-item.nav-item, .site-header-nav .nav-item,
.reader-nav .reader-nav-view-type-selector,
.reader-nav .reader-nav-text-item,
.reader-nav .reader-nav-settings {
display:block !important;
float:none !important;
width:auto !important;
border:none !important;
margin:0 !important;
max-width:initial !important;
min-width:0 !important;
background-position: 8px 50% !important;
}
.reader-nav .reader-nav-settings .reader-nav-tooltip {
position:relative !important;
width:auto !important;
clear:both !important;
min-width:0 !important;
margin:0 !important;
}
.reader-nav .reader-nav-settings .reader-nav-tooltip li a {
padding-right:10px !important;
}
.reader-nav .reader-nav-item:hover, .reader-nav .reader-nav-text-item:hover {
background-color:#FDFDFD !important;
}
.story-detail-view {height:auto !important;}
}

@media (max-width:516px) {
.story-title, .story-meta, .btn-toggle-read-state, .detail-body {
margin-left:15px !important;
}
.feed-stream-header {padding:0.8em 0 !important;}
.col-left:after {
margin-top:10px !important;
  padding-left: 10px !important;
}
.story-title {
font-size:14pt !important;
}
}
}
