css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lms.neumont.edu") {
    /* HEADER */
    
    div#header.no-print{
        background-color:#444 !important;
        border-bottom-color: rgb(255, 204, 0) !important;
    }
    
    ul#menu > li.menu-item > a{
        color:white !important;
    }
    
    /********************************BROKEN******************/
    li.menu-item.hover > a.menu-item-title{
        color:#444 !important;
    }
    /*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^BROKEN^^^^^^^^^^^^^^^^^^*/
    
    a#header-logo{
        background: url("http://neumont.edu/cmsimages/neumont_logo.png") no-repeat scroll 0px 0px / 100% auto transparent !important;
        top: 10px;
        left: 10px !important;
        height:55px !important;
        width: 98px !important;
    }
    
    div#topbar > ul#identity{
        background: rgb(255, 204, 0) !important;
    }
    
    div#topbar > ul#identity > li > a{
        color: #444 !important;
    }
    
    /* END HEADER */
    
    /* GLOBAL STUFF */
    
    body{
        background: white !important;
    }
    
    .list-view > nav > ul > li > a:hover{
        background-color: #ddd !important;
    }
    
    .list-view a.active{
        background-color: #444 !important;
        color: rgb(255, 204, 0) !important;
        color: white !important;
        border-right: 3px solid rgb(255, 204, 0);
        border-bottom-color: #aaa !important;
        border-top-color: #ddd !important;
    }
    
    .list-view > nav > ul > li > a.active:hover{
        background-color: #666 !important;
    }
    
    ul#section-tabs{
        background-color: #fafafa;
    }
    
    div#section-tabs-header.h1{
        background-image: linear-gradient(to bottom, #555, #444) !important;
        background-color:#444 !important;
        color:white !important;
        text-shadow: none !important;
        font-weight: bold !important;
    }
    
    .btn-primary{
        background-image: linear-gradient(to bottom, rgb(255, 204, 0), rgb(250, 200, 0)) !important;
        color: #444 !important;
        text-shadow: 0px -1px 1px white !important;
        border-top-color:rgb(250, 200, 0) !important;
        border-bottom-color:rgb(245, 195, 0) !important;
    }
    
    .btn-primary:hover, .btn-primary:active, .btn-primary.active, .btn-primary.disabled, .btn-primary[disabled]{
        background-color:rgb(250, 200, 0) !important;
    }
    
    a.footer-logo{
        opacity:0 !important;
    }
    
    /* END GLOBAL STUFF */
    
    /* GRADES PAGE */
    
    table#grades_summary tr.assignment_graded > *{
        background-color:#eee !important;
    }
    
    table#grades_summary tr.editable > *{
        -moz-transition: background-color 0.3s;
    }
    
    table#grades_summary tr.editable:hover > *{
        background-color:#444 !important;
        color:white !important;
    }
    
    table#grades_summary tr.editable:hover > * a{
        color: rgb(255, 204, 0) !important;
    }
    
    /* END GRADES PAGE */
    
    /* DISCUSSIONS PAGE */
    
    .item-group, .discussion-collections{
        background-color:#eee !important;
    }

    /* This eliminates the blue unread replies number if it's 0 */
    span.new-items[title="No unread replies"]{
        display:none;
    }
    
    /* END DISCUSSIONS PAGE */
}

@-moz-document url("https://lms.neumont.edu/login") {

    /* LOGIN PAGE */
    
    div#modal-box-inner > h1{
        background: url(http://www.neumont.edu/cmsimages/neumont_logo.png) white center no-repeat !important;
        border-bottom-right-radius: 20px;
        width:140px;
        height:140px !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }
    
    div#modal-box.login-box{
        background: white !important;
        border-bottom-right-radius: 30px;
    }
    
    input.text{
        border-color: #444 !important;
    }
    
    body{
        background: #444 !important;
    }
    
    /* END LOGIN PAGE */

}
