css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("www.tumblr.com") , url-prefix("https://www.tumblr.com") {
#logo {height: 0 !important;
	width: 0 !important;
	padding-left: 240px !important;
	padding-top: 60px !important;
	background: url(http://i.imgur.com/Fl4LZyh.gif) no-repeat !important;}

ol#posts li.notification {

  background-color: #B94CDA !important;

  border-top: 1px solid #76079A !important;

  border-left: 1px solid #76079A !important;

  border-bottom: 1px solid #76079A !important;

  border-right: 1px solid #76079A !important;

  color: #7F3795 !important;

  text-decoration: none;

}

.answer_container {

  background-color: #D18AFA !important;

}


body {

background: #BD95D1 url('http://i.imgur.com/HdjTs3M.gif') !important;
background-position: right bottom !important;
background-attachment:fixed !important;
background-repeat:no-repeat !important;


}

#posts .notification a:not(.avatar_frame) {

color: rgba(17, 65, 163, 1) !important;

font-weight: bold !important;

text-decoration: none !important;

border-bottom: 0px solid #ffffff;

-webkit-background-clip: padding-box;

}



#posts .notification blockquote {

  border-left: solid 3px #64059B !important;

}



#posts .notification .avatar_frame {

background-color: #ffffff !important;

border: 1px solid #ffffff !important;

position: absolute;

top: -1px;

left: -55px;

overflow: hidden;

display: inline-block;

}












.post.is_mine.with_avatar.new_post {

  color: #ffffff !important;

  font-family:courier, monospace !important;

  background: rgba(0,0,0,0.50) !important; 

}










#posts .post.new_post .new_post_label {

-webkit-font-smoothing: antialiased;

opacity: .9;

font-size: 14px;

font-weight: normal;

color: #ffffff;

text-decoration: none;

text-transform: uppercase;

text-align: center;

width: 74px;

position: absolute;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(185, 92, 228, .5) !important; 

}







.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;

}



#content_top {

  width: 0px !important;

  height: 0px !important;




}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px rgba(00,00,00, 0.50) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}




#content_bottom {

  width: 0px !important;

  height: 0px !important;









}





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}





LABEL {

  color: rgba(0,0,0, 1.0) !important; 

}




#footer {

  display: none !important; 

}



.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: rgba(0, 0, 0, 0.0) !important; 

}





#new_post .arrow {

  opacity: 0.50 !important;

  margin-left: -1px !important; 

}
}
