.sharingWidgetBox{background-color:#333!important;}
.sharingWidget{background-color:#111!important;}

h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h1 a:hover span, h2 a:hover span, h3 a:hover span, h4 a:hover span {
    color: #ffffff!important;
}


.tabNavigation .menuValue, #navstyle .menuValue, .smallTorrentBadge {
    color: #111!important;
    border-left: 1px solid rgb(100, 100, 100)!important;
    border-bottom: 1px solid rgb(88, 88, 88)!important;
    background: -moz-linear-gradient(center top , rgb(150, 150, 150) 1%, rgb(170,170,170) 6%, rgb(180, 180, 180) 11%, rgb(120,120,120) 100%) repeat scroll 0% 0% transparent!important;
