}
.contentTabContainer *{color:white!important;}

.torrentDownloaded {
    opacity: 0.2;
}

.data th,.sliderbox h3{color:#eee!important;}

.siteButton, .nohover:hover .siteButton, .icon16{background: -moz-linear-gradient(top, #525559 0%, #2e2d33 36%, #0f1316 100%)!important;}

.sliderbox ul li:hover{background-color:rgba(30,30,30,0.6)!important;}

