color:#999!important}

.headmenu{background:none!important;}
.toppad10px{padding:0px!important;}

.headmainpart {
height: 56px!important;}
#navstyle{background:none!important;
background-color:rgb(15,15,15)!important;}
.darkbrownbg {background-color:rgb(15,15,15)!important;}
.mainpart,footer{
background-color:rgba(0,0,0,0.8)!important;}
.tagcloud{
background-color:#151515!important;
background: -moz-linear-gradient(top, #101010 0%, #151515 100%)!important;}

h1 a, h2 a, h3 a, h4 a, h5 a{color:#ddd!important;}
h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover{color:#fff!important;}


.tagcloud a.tag3{color:#fff!important;font-size:100%!important;opacity:0.3;}

.tagcloud a.tag5{color:#fff!important;font-size:125%!important;opacity:0.4;}

.tagcloud a.tag7{color:#fff!important;font-size:150%!important;opacity:0.5;}

.tagcloud a.tag9{color:#fff!important;font-size:175%!important;opacity:0.6;}

.tagcloud a:hover{opacity:1;}
tr.odd{background-color:rgba(20,20,20,0.3)!important;}
tr.even{background-color:rgba(0,0,0,0.3)!important;}
tr.odd,tr.even{color:rgb(180,180,180)!important;}
tr.odd a,tr.even a {color:rgb(220,220,220)!important;}
tr.even:hover{background:rgba(20,20,20,0.4)!important;}
tr.odd:hover{background:rgba(30,30,30,0.4)!important;}

.showmore{background: -moz-linear-gradient(top, #202020 0%, #101010 100%)!important;
border:0px!important;}
.showmore:hover{background: -moz-linear-gradient(top, #202020 0%, #131313 100%)!important;
border:0px!important;}
/*border*/
table tbody *,#navstyle{border-color:rgba(0,0,0,0.5)!important;}

hr.tabsSeparator{background-color:rgba(20,20,20,0.8)!important;}
.letterssort{background-color:transparent!important;
border:0px!important;}
h1, h2, h3, h4, h5{color:#eee!important;}

.formtable {color:white!important;}

.hideSidebar{border:none!important;}
.firstPost strong{background-color:rgba(50,50,50,0.7)!important;}
.lightivorybg, .versionsUnFolded, .lightivorybg:hover{background-color:rgba(50,50,50,0.7)!important;}

#communityBlock .commentbody,.profileCloud,.quote, blockquote, .accentbox {background-color:rgba(0,0,0,0.7)!important;
color:white!important;}

.achTable a{color:black!important;}

.achTable{color:#eee!important;}

.torrentFileList tr.last .torTree,.firstPost div {background-color:transparent!important;}
#ul_top > tbody:nth-child(1) > tr:nth-child(1n+0){color:#eee!important;}
#ul_top > tbody:nth-child(1) > tr:nth-child(1n+0):hover{background-color:transparent!important;}
